/* ============================================
   NED — shadcn/ui Design System
   Clean · Minimal · Terminal Green Accent · Light/Dark
   ============================================ */

/* --- Design Tokens (Light Mode) --- */
:root {
  --background: #ffffff;
  --foreground: #0f172a;
  --card: #ffffff;
  --card-foreground: #0f172a;
  --muted: #f1f5f9;
  --muted-foreground: #64748b;
  --accent: #f0fdf4;
  --accent-foreground: #14532d;
  --primary: #15803d;
  --primary-foreground: #ffffff;
  --border: #e2e8f0;
  --ring: #15803d;

  /* Backward-compat aliases */
  --ned-bg: var(--background);
  --ned-fg: var(--foreground);
  --ned-muted: var(--muted);
  --ned-muted-fg: var(--muted-foreground);
  --ned-accent: var(--primary);
  --ned-accent-fg: var(--primary-foreground);
  --ned-border: var(--border);
  --ned-card: var(--card);
}

/* --- Dark Mode --- */
@media (prefers-color-scheme: dark) {
  :root {
    --background: #09090b;
    --foreground: #fafafa;
    --card: #18181b;
    --card-foreground: #fafafa;
    --muted: #27272a;
    --muted-foreground: #a1a1aa;
    --accent: #27272a;
    --accent-foreground: #22c55e;
    --primary: #16a34a;
    --primary-foreground: #ffffff;
    --border: #27272a;
    --ring: #16a34a;
  }
}

/* --- Element Base Styles --- */

body {
  background: var(--background);
  color: var(--foreground);
  font-family: 'Inter', system-ui, sans-serif;
  line-height: 1.5;
}

h1, h2, h3, h4, h5, h6 {
  font-weight: 600;
  line-height: 1.2;
  letter-spacing: -0.025em;
}

h1 { font-size: 1.875rem; }
h2 { font-size: 1.5rem; }
h3 { font-size: 1.25rem; }
h4 { font-size: 1.125rem; }
h5 { font-size: 1rem; }
h6 { font-size: 0.875rem; }

a {
  color: var(--primary);
  text-decoration: none;
  transition: color 0.2s;
}
a:hover {
  text-decoration: underline;
}

button {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  height: 2.5rem;
  padding: 0 1rem;
  background: var(--primary);
  color: var(--primary-foreground);
  font-family: inherit;
  font-size: 0.875rem;
  font-weight: 500;
  border: none;
  border-radius: 0.375rem;
  cursor: pointer;
  transition: opacity 0.2s;
  white-space: nowrap;
}
button:hover {
  opacity: 0.9;
}
button:active {
  opacity: 0.8;
}
button:focus-visible {
  outline: 2px solid var(--ring);
  outline-offset: 2px;
}

input, textarea {
  background: var(--background);
  color: var(--foreground);
  font-family: inherit;
  font-size: 0.875rem;
  border: 1px solid var(--border);
  border-radius: 0.375rem;
  padding: 0.5rem 0.75rem;
  width: 100%;
  outline: none;
  transition: border-color 0.2s, box-shadow 0.2s;
}
input:focus, textarea:focus {
  border-color: var(--ring);
  box-shadow: 0 0 0 2px color-mix(in srgb, var(--ring) 25%, transparent);
}
input::placeholder, textarea::placeholder {
  color: var(--muted-foreground);
}

table {
  width: 100%;
  border-collapse: collapse;
  border: 1px solid var(--border);
  border-radius: 0.5rem;
  overflow: hidden;
}
th {
  font-weight: 500;
  font-size: 0.75rem;
  color: var(--muted-foreground);
  background: var(--muted);
  text-align: left;
  padding: 0.625rem 1rem;
}
td {
  border-bottom: 1px solid var(--border);
  padding: 0.625rem 1rem;
  font-size: 0.875rem;
}
tr:last-child td {
  border-bottom: none;
}

hr {
  border: none;
  border-top: 1px solid var(--border);
  margin: 1.5rem 0;
}

code {
  background: var(--muted);
  color: var(--accent-foreground);
  padding: 0.15em 0.4em;
  font-size: 0.875em;
  border-radius: 0.25rem;
}

pre {
  background: var(--muted);
  padding: 1rem;
  overflow-x: auto;
  margin: 0.75rem 0;
  border-radius: 0.5rem;
}
pre code {
  background: none;
  padding: 0;
}

/* --- Button Variants --- */

button.ghost {
  background: transparent;
  color: var(--foreground);
}
button.ghost:hover {
  background: var(--muted);
  opacity: 1;
}

button.outline {
  background: transparent;
  border: 1px solid var(--border);
  color: var(--foreground);
}
button.outline:hover {
  background: var(--muted);
  opacity: 1;
}

button.sm {
  height: 2.25rem;
  padding: 0 0.875rem;
  font-size: 0.8125rem;
}

button.xs {
  height: 1.75rem;
  padding: 0 0.625rem;
  font-size: 0.75rem;
}

button.icon {
  width: 2.5rem;
  height: 2.5rem;
  padding: 0;
}
button.icon.sm {
  width: 2rem;
  height: 2rem;
}

/* --- Badge --- */

.badge {
  display: inline-flex;
  align-items: center;
  padding: 0.125rem 0.5rem;
  font-size: 0.7rem;
  font-weight: 500;
  background: var(--muted);
  color: var(--muted-foreground);
  border-radius: 9999px;
}
.badge.success { background: #dcfce7; color: #166534; }
.badge.error   { background: #fee2e2; color: #991b1b; }
.badge.warning { background: #fef3c7; color: #92400e; }
.badge.info    { background: #dbeafe; color: #1e40af; }

@media (prefers-color-scheme: dark) {
  .badge.success { background: #166534; color: #4ade80; }
  .badge.error   { background: #7f1d1d; color: #f87171; }
  .badge.warning { background: #78350f; color: #fbbf24; }
  .badge.info    { background: #1e3a5f; color: #60a5fa; }
}

/* --- Card --- */

.card {
  border: 1px solid var(--border);
  background: var(--card);
  padding: 1.5rem;
  border-radius: 0.5rem;
  box-shadow: 0 1px 2px 0 rgb(0 0 0 / 0.05);
  transition: border-color 0.2s;
}

/* --- Chat Messages (CLI-style) --- */

.chat-msg {
  padding: 0.75rem 1rem;
  max-width: 100%;
  border-radius: 0;
  background: none;
  border: none;
  border-top: 1px solid var(--border);
}
.chat-msg:first-child { border-top: none; }

.chat-msg.user {
  margin-left: 0;
  background: none;
  color: var(--foreground);
}
.chat-msg.ai {
  margin-right: 0;
  background: none;
  color: var(--foreground);
}

/* Role label styling */
.chat-msg.user .msg-meta span:first-child {
  color: var(--primary);
  font-weight: 600;
}
.chat-msg.ai .msg-meta span:first-child {
  color: var(--muted-foreground);
  font-weight: 600;
}

/* --- Sidebar --- */

#sidebar-toggle {
  display: none;
}

.sidebar {
  position: fixed;
  top: 0;
  left: 0;
  width: 16rem;
  height: 100dvh;
  background: var(--card);
  border-right: 1px solid var(--border);
  transform: translateX(-100%);
  transition: transform 0.2s ease;
  z-index: 50;
  overflow-y: auto;
  padding: 1.5rem;
  border-radius: 0 0.75rem 0.75rem 0;
}

#sidebar-toggle:checked ~ .sidebar-container .sidebar {
  transform: translateX(0);
}

.sidebar-container::before {
  content: '';
  position: fixed;
  inset: 0;
  background: rgba(0, 0, 0, 0.4);
  z-index: 40;
  opacity: 0;
  pointer-events: none;
  transition: opacity 0.2s ease;
}
#sidebar-toggle:checked ~ .sidebar-container::before {
  opacity: 1;
  pointer-events: auto;
}

.sidebar nav a {
  display: block;
  padding: 0.5rem 0.75rem;
  color: var(--muted-foreground);
  font-size: 0.875rem;
  font-weight: 500;
  text-decoration: none;
  border-radius: 0.375rem;
  transition: background 0.15s, color 0.15s;
}
.sidebar nav a:hover {
  background: var(--muted);
  color: var(--foreground);
  text-decoration: none;
}

/* --- Utility Classes --- */

.section-heading {
  font-size: 1.125rem;
  font-weight: 600;
  margin-bottom: 1rem;
  letter-spacing: -0.025em;
}

.msg-meta {
  font-size: 0.65rem;
  text-transform: uppercase;
  letter-spacing: 0.05em;
  color: var(--muted-foreground);
}

.notification-unread {
  border-left: 3px solid var(--primary);
}

/* --- Notifications Dropdown --- */

.notifications-dropdown {
  position: absolute;
  top: calc(100% + 0.5rem);
  right: 0;
  z-index: 100;
  width: 360px;
  max-height: 480px;
  background: var(--card);
  border: 1px solid var(--border);
  border-radius: 0.5rem;
  box-shadow: 0 10px 15px -3px rgb(0 0 0 / 0.1), 0 4px 6px -4px rgb(0 0 0 / 0.1);
  display: flex;
  flex-direction: column;
  animation: ctx-menu-in 0.12s ease-out;
}

.notifications-dropdown-header {
  padding: 0.75rem 1rem;
  border-bottom: 1px solid var(--border);
  flex-shrink: 0;
}

.notifications-dropdown-list {
  overflow-y: auto;
  flex: 1;
  min-height: 0;
}

.notifications-dropdown-list .card {
  border-radius: 0;
  border-left: none;
  border-right: none;
  border-top: none;
  box-shadow: none;
  padding: 0.75rem 1rem;
}
.notifications-dropdown-list .card:last-child {
  border-bottom: none;
}

.notifications-dropdown-list .notification-unread {
  border-left: 3px solid var(--primary);
}

.notifications-dropdown-footer {
  border-top: 1px solid var(--border);
  padding: 0.5rem;
  flex-shrink: 0;
}

@media (max-width: 639px) {
  .notifications-dropdown {
    width: calc(100vw - 1rem);
    right: -0.5rem;
  }
}

/* --- Notification Card Submenu --- */

.notification-submenu {
  position: relative;
}

.notification-submenu > summary {
  cursor: pointer;
  list-style: none;
  display: inline-flex;
  align-items: center;
  gap: 0.375rem;
  height: 1.75rem;
  padding: 0 0.625rem;
  font-size: 0.75rem;
  font-weight: 500;
  background: transparent;
  border: 1px solid var(--border);
  color: var(--foreground);
  border-radius: 0.375rem;
  transition: background 0.15s;
}
.notification-submenu > summary:hover {
  background: var(--muted);
}
.notification-submenu > summary::-webkit-details-marker {
  display: none;
}
.notification-submenu > summary::after {
  content: '';
  display: inline-block;
  width: 0;
  height: 0;
  border-left: 4px solid transparent;
  border-right: 4px solid transparent;
  border-top: 4px solid var(--muted-foreground);
  margin-left: 0.25rem;
}

.notification-submenu-panel {
  position: absolute;
  bottom: calc(100% + 4px);
  left: 0;
  z-index: 50;
  min-width: 220px;
  max-width: 320px;
  background: var(--card);
  border: 1px solid var(--border);
  border-radius: 0.5rem;
  box-shadow: 0 4px 6px -1px rgb(0 0 0 / 0.1), 0 2px 4px -2px rgb(0 0 0 / 0.1);
  padding: 0.25rem;
  animation: ctx-menu-in 0.12s ease-out;
}

.notification-submenu-item {
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  gap: 0.125rem;
  width: 100%;
  height: auto;
  padding: 0.375rem 0.625rem;
  background: transparent;
  color: var(--foreground);
  font-size: 0.8125rem;
  font-weight: 400;
  border: none;
  border-radius: 0.25rem;
  cursor: pointer;
  text-align: left;
}
.notification-submenu-item:hover {
  background: var(--muted);
  opacity: 1;
}

.notification-submenu-new {
  color: var(--primary);
}

.notification-submenu-section {
  font-weight: 500;
}

.notification-submenu-divider {
  height: 1px;
  background: var(--border);
  margin: 0.25rem 0;
}

.nav-trigger {
  cursor: pointer;
  display: inline-flex;
  align-items: center;
  justify-content: center;
  width: 2.5rem;
  height: 2.5rem;
  border-radius: 0.375rem;
  transition: background 0.15s;
}
.nav-trigger:hover {
  background: var(--muted);
}

.notification-badge {
  background: var(--primary);
  color: var(--primary-foreground);
  font-size: 0.6rem;
  padding: 0.1rem 0.35rem;
  border-radius: 9999px;
  font-weight: 600;
}

/* --- Prose Bubble (Markdown in Chat) --- */

.prose-bubble > *:first-child { margin-top: 0; }
.prose-bubble > *:last-child { margin-bottom: 0; }

.prose-bubble p { margin: 0.4em 0; }
.prose-bubble h1, .prose-bubble h2, .prose-bubble h3,
.prose-bubble h4, .prose-bubble h5, .prose-bubble h6 {
  font-weight: 600;
  margin: 0.6em 0 0.3em;
}
.prose-bubble h1 { font-size: 1.25em; }
.prose-bubble h2 { font-size: 1.15em; }
.prose-bubble h3 { font-size: 1.05em; }

.prose-bubble ul, .prose-bubble ol { margin: 0.4em 0; padding-left: 1.4em; }
.prose-bubble ul { list-style: disc; }
.prose-bubble ol { list-style: decimal; }
.prose-bubble li { margin: 0.15em 0; }

.prose-bubble code {
  background: var(--muted);
  color: var(--accent-foreground);
  padding: 0.15em 0.35em;
  font-size: 0.875em;
  border-radius: 0.25rem;
}
.prose-bubble pre {
  background: var(--muted);
  padding: 0.75em;
  overflow-x: auto;
  margin: 0.5em 0;
  border-radius: 0.375rem;
}
.prose-bubble pre code {
  background: none;
  padding: 0;
  font-size: 0.85em;
}

.prose-bubble blockquote {
  border-left: 3px solid var(--border);
  padding-left: 0.75em;
  margin: 0.4em 0;
  color: var(--muted-foreground);
}

.prose-bubble table { border-collapse: collapse; margin: 0.5em 0; width: 100%; }
.prose-bubble th, .prose-bubble td {
  border: 1px solid var(--border);
  padding: 0.3em 0.6em;
  text-align: left;
}
.prose-bubble th { font-weight: 600; background: var(--muted); }

.prose-bubble a { color: var(--primary); text-decoration: underline; }
.prose-bubble a:hover { opacity: 0.8; }

.prose-bubble hr { border: none; border-top: 1px solid var(--border); margin: 0.6em 0; }

/* --- Agent Steps (streaming progress) --- */

.agent-steps {
  display: flex;
  flex-direction: column;
  gap: 0.125rem;
  margin-bottom: 0.5rem;
}
.agent-step {
  display: flex;
  align-items: center;
  gap: 0.5rem;
  font-size: 0.75rem;
  color: var(--muted-foreground);
}
.agent-step-icon {
  width: 0.5rem;
  height: 0.5rem;
  border-radius: 50%;
  background: var(--muted-foreground);
  flex-shrink: 0;
}
/* Pulse the active (last) step */
.agent-step:last-child .agent-step-icon {
  background: var(--primary);
  animation: step-pulse 1.5s ease-in-out infinite;
}
@keyframes step-pulse {
  0%, 100% { opacity: 1; }
  50% { opacity: 0.3; }
}

/* --- Custom Scrollbar --- */

.messages::-webkit-scrollbar { width: 6px; }
.messages::-webkit-scrollbar-track { background: transparent; }
.messages::-webkit-scrollbar-thumb { background: var(--border); border-radius: 3px; }
.messages::-webkit-scrollbar-thumb:hover { background: var(--muted-foreground); }

/* --- Reduced Motion --- */

@media (prefers-reduced-motion: reduce) {
  *, *::before, *::after {
    transition: none !important;
    animation: none !important;
  }
}

/* --- Chat Footer --- */

.chat-footer {
  display: flex;
  flex-direction: column;
  border-top: 1px solid var(--border);
  background: var(--card);
  min-height: 2.5rem;
  max-height: calc(100dvh - 80px);
  overflow: hidden;
  flex-shrink: 0;
}

.chat-footer-handle {
  height: 6px;
  cursor: ns-resize;
  background: transparent;
  position: relative;
  flex-shrink: 0;
}
.chat-footer-handle::after {
  content: '';
  position: absolute;
  left: 50%;
  top: 50%;
  transform: translate(-50%, -50%);
  width: 32px;
  height: 3px;
  border-radius: 2px;
  background: var(--border);
  transition: background 0.15s;
}
.chat-footer-handle:hover::after {
  background: var(--muted-foreground);
}

.chat-tab-bar {
  display: flex;
  align-items: center;
  gap: 0;
  border-bottom: 1px solid var(--border);
  padding: 0 0.25rem;
  flex-shrink: 0;
  overflow-x: auto;
  min-height: 2rem;
}

.chat-tabs {
  display: flex;
  align-items: center;
  gap: 0;
  overflow-x: auto;
  flex: 1;
  min-width: 0;
}

.chat-tab {
  display: inline-flex;
  align-items: center;
  gap: 0.375rem;
  padding: 0.25rem 0.5rem;
  font-size: 0.75rem;
  font-weight: 500;
  color: var(--muted-foreground);
  background: transparent;
  border: none;
  border-bottom: 2px solid transparent;
  border-radius: 0;
  height: 2rem;
  cursor: pointer;
  white-space: nowrap;
  transition: color 0.15s, border-color 0.15s;
}
.chat-tab:hover {
  color: var(--foreground);
  opacity: 1;
}
.chat-tab.active {
  color: var(--foreground);
  border-bottom-color: var(--primary);
}

.chat-tab-title {
  max-width: 120px;
  overflow: hidden;
  text-overflow: ellipsis;
}

.chat-tab-close {
  font-size: 0.875rem;
  line-height: 1;
  opacity: 0.5;
  cursor: pointer;
  padding: 0 0.125rem;
}
.chat-tab-close:hover {
  opacity: 1;
}

.chat-tab-new {
  flex-shrink: 0;
  width: 2rem;
  height: 2rem;
  font-size: 1rem;
  padding: 0;
}

/* Two-level tab bars */
.chat-tab-bar.canvas-level {
  background: var(--muted);
  border-bottom: none;
}
.chat-tab-bar.conversation-level {
  min-height: 1.75rem;
  padding-left: 0.5rem;
}
.chat-tab-bar.conversation-level .chat-tab {
  font-size: 0.6875rem;
  height: 1.75rem;
}
.chat-tab-bar.conversation-level .chat-tab-new {
  width: 1.75rem;
  height: 1.75rem;
  font-size: 0.875rem;
}

.chat-panels {
  flex: 1;
  position: relative;
  overflow: hidden;
  min-height: 0;
}

.chat-panel {
  display: none;
  position: absolute;
  inset: 0;
  flex-direction: column;
  overflow: hidden;
}
.chat-panel.active {
  display: flex;
}

.chat-panel-inner {
  display: flex;
  flex-direction: column;
  height: 100%;
  overflow: hidden;
}

.chat-panel-messages {
  flex: 1;
  overflow-y: auto;
  padding: 0.75rem;
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
}
.chat-panel-messages::-webkit-scrollbar { width: 5px; }
.chat-panel-messages::-webkit-scrollbar-track { background: transparent; }
.chat-panel-messages::-webkit-scrollbar-thumb { background: var(--border); border-radius: 3px; }

/* Compact message styles inside footer */
.chat-panel-messages .chat-msg {
  padding: 0.5rem 0.75rem;
  font-size: 0.8125rem;
  max-width: 100%;
}
.chat-panel-messages .msg-meta {
  font-size: 0.6rem;
}
.chat-panel-messages .prose-bubble p {
  margin: 0.25em 0;
}

.chat-panel-form {
  display: flex;
  align-items: flex-end;
  gap: 0.5rem;
  padding: 0.5rem 0.75rem;
  border-top: 1px solid var(--border);
  flex-shrink: 0;
}

.chat-panel-input {
  flex: 1;
  min-width: 0;
  resize: none;
  font-size: 0.8125rem;
  padding: 0.375rem 0.625rem;
  max-height: 120px;
}

.chat-panel-loading {
  padding: 2rem;
  text-align: center;
  color: var(--muted-foreground);
  font-size: 0.8125rem;
}

/* --- Conversation Canvas --- */

.conversation-canvas {
  display: none;
  position: absolute;
  inset: 0;
  overflow: hidden;
  background: var(--background);
}
.conversation-canvas.active {
  display: block;
}

/* Dot grid background */
.canvas-dot-grid {
  position: absolute;
  inset: 0;
  background-image: radial-gradient(circle, var(--border) 1px, transparent 1px);
  background-size: 24px 24px;
  pointer-events: none;
  z-index: 0;
}

/* The pannable/zoomable world */
.canvas-world {
  position: absolute;
  top: 0;
  left: 0;
  transform-origin: 0 0;
  will-change: transform;
  z-index: 1;
}

/* Individual canvas component */
.canvas-component {
  position: absolute;
  background: var(--card);
  border: 1px solid var(--border);
  border-radius: 0.5rem;
  box-shadow: 0 1px 3px 0 rgb(0 0 0 / 0.1), 0 1px 2px -1px rgb(0 0 0 / 0.1);
  cursor: grab;
  user-select: none;
  transition: box-shadow 0.15s, border-color 0.15s;
}
.canvas-component:hover {
  border-color: var(--primary);
  box-shadow: 0 4px 6px -1px rgb(0 0 0 / 0.1), 0 2px 4px -2px rgb(0 0 0 / 0.1);
}
.canvas-component.dragging {
  cursor: grabbing;
  box-shadow: 0 10px 15px -3px rgb(0 0 0 / 0.2), 0 4px 6px -4px rgb(0 0 0 / 0.2);
  border-color: var(--primary);
  z-index: 9999 !important;
  opacity: 0.95;
}
.canvas-component.canvas-component-highlight {
  border-color: var(--primary);
  box-shadow: 0 0 0 3px color-mix(in srgb, var(--primary) 30%, transparent), 0 4px 6px -1px rgb(0 0 0 / 0.1);
  animation: comp-highlight-pulse 0.6s ease-in-out 2;
}
@keyframes comp-highlight-pulse {
  0%, 100% { box-shadow: 0 0 0 3px color-mix(in srgb, var(--primary) 30%, transparent); }
  50% { box-shadow: 0 0 0 6px color-mix(in srgb, var(--primary) 15%, transparent); }
}

.canvas-component-content {
  padding: 1rem;
  font-size: 0.875rem;
  overflow: hidden;
}
.canvas-component-content[contenteditable="true"] {
  outline: 2px solid var(--ring);
  outline-offset: -2px;
  border-radius: 0.375rem;
  cursor: text;
  overflow: auto;
}
.canvas-component-content > *:first-child { margin-top: 0; }
.canvas-component-content > *:last-child { margin-bottom: 0; }

/* Canvas context menu */
.canvas-context-menu {
  position: fixed;
  z-index: 10000;
  min-width: 180px;
  background: var(--card);
  border: 1px solid var(--border);
  border-radius: 0.5rem;
  box-shadow: 0 4px 6px -1px rgb(0 0 0 / 0.1), 0 2px 4px -2px rgb(0 0 0 / 0.1);
  padding: 0.25rem;
  animation: ctx-menu-in 0.12s ease-out;
}

@keyframes ctx-menu-in {
  from { opacity: 0; transform: scale(0.95); }
  to { opacity: 1; transform: scale(1); }
}

.canvas-context-menu-item {
  display: flex;
  align-items: center;
  gap: 0.5rem;
  width: 100%;
  height: auto;
  padding: 0.375rem 0.625rem;
  background: transparent;
  color: var(--foreground);
  font-size: 0.8125rem;
  font-weight: 400;
  border: none;
  border-radius: 0.25rem;
  cursor: pointer;
  text-align: left;
  justify-content: flex-start;
}
.canvas-context-menu-item:hover {
  background: var(--muted);
  opacity: 1;
}

.canvas-context-menu-item.destructive {
  color: #ef4444;
}
.canvas-context-menu-item.destructive:hover {
  background: #fef2f2;
}
@media (prefers-color-scheme: dark) {
  .canvas-context-menu-item.destructive:hover {
    background: #451a1a;
  }
}

.canvas-context-menu-separator {
  height: 1px;
  background: var(--border);
  margin: 0.25rem 0;
}

/* Zoom indicator */
.canvas-zoom-indicator {
  position: absolute;
  bottom: 0.75rem;
  right: 0.75rem;
  background: var(--card);
  border: 1px solid var(--border);
  border-radius: 0.375rem;
  padding: 0.25rem 0.5rem;
  font-size: 0.7rem;
  color: var(--muted-foreground);
  z-index: 10;
  pointer-events: none;
  display: flex;
  align-items: center;
  gap: 0.25rem;
}
.canvas-scroll-lock-btn {
  all: unset;
  cursor: pointer;
  padding: 0.125rem;
  border-radius: 0.25rem;
  line-height: 1;
  font-size: 0.8rem;
  pointer-events: auto;
}
.canvas-scroll-lock-btn:hover {
  background: var(--muted);
}

/* --- Scroll Lock Mode --- */
.conversation-canvas.scroll-locked {
  overflow-y: auto;
}
.conversation-canvas.scroll-locked .canvas-dot-grid {
  display: none;
}

/* --- Heartbeat Grid --- */

@keyframes heartbeat-pulse {
  0%, 100% { opacity: 1; transform: scale(1); }
  50% { opacity: 0.7; transform: scale(1.15); }
}

.hb-grid table { width: 100%; }
.hb-grid th, .hb-grid td { vertical-align: middle; }

.hb-cell {
  display: inline-block;
  width: 16px;
  height: 16px;
  border-radius: 3px;
  background: var(--muted);
  flex-shrink: 0;
  cursor: default;
  transition: transform 0.1s ease;
}

.hb-cell:hover {
  transform: scale(1.3);
  z-index: 1;
}

.hb-cell.success { background: #10b981; }
.hb-cell.skipped { background: var(--muted-foreground); opacity: 0.4; }
.hb-cell.error { background: #ef4444; }

@media (prefers-color-scheme: dark) {
  .hb-cell.skipped { background: #52525b; opacity: 1; }
}

/* --- Mobile --- */

@media (max-width: 639px) {
  main.page-content { padding: 0.75rem; }
  .card { padding: 1rem; }
  button { min-height: 2.75rem; }
  button.icon { width: 2.75rem; height: 2.75rem; }
  .chat-msg { max-width: 100%; }
  .sidebar nav a { min-height: 2.75rem; display: flex; align-items: center; }

  .chat-tab { padding: 0.25rem 0.375rem; font-size: 0.6875rem; }
  .chat-tab-title { max-width: 80px; }
  .chat-panel-messages .chat-msg { max-width: 100%; }
}

/* ============================================
   NED — shadcn/ui Design System
   Clean · Minimal · Terminal Green Accent · Light/Dark
   ============================================ */

/* --- Design Tokens (Light Mode) --- */
:root {
  --background: #f8f9fa;
  --foreground: #0f172a;
  --card: #f1f3f5;
  --card-foreground: #0f172a;
  --muted: #e9ecef;
  --muted-foreground: #64748b;
  --accent: #f0fdf4;
  --accent-foreground: #14532d;
  --primary: #15803d;
  --primary-foreground: #ffffff;
  --border: #e2e8f0;
  --ring: #15803d;

  /* Backward-compat aliases */
  --ned-bg: var(--background);
  --ned-fg: var(--foreground);
  --ned-muted: var(--muted);
  --ned-muted-fg: var(--muted-foreground);
  --ned-accent: var(--primary);
  --ned-accent-fg: var(--primary-foreground);
  --ned-border: var(--border);
  --ned-card: var(--card);
}

/* --- Dark Mode --- */
@media (prefers-color-scheme: dark) {
  :root {
    --background: #09090b;
    --foreground: #fafafa;
    --card: #18181b;
    --card-foreground: #fafafa;
    --muted: #27272a;
    --muted-foreground: #a1a1aa;
    --accent: #27272a;
    --accent-foreground: #22c55e;
    --primary: #16a34a;
    --primary-foreground: #ffffff;
    --border: #27272a;
    --ring: #16a34a;
  }
}

/* --- Element Base Styles --- */

body {
  background: var(--background);
  color: var(--foreground);
  font-family: 'Inter', system-ui, sans-serif;
  line-height: 1.5;
}

h1, h2, h3, h4, h5, h6 {
  font-weight: 600;
  line-height: 1.2;
  letter-spacing: -0.025em;
}

h1 { font-size: 1.875rem; }
h2 { font-size: 1.5rem; }
h3 { font-size: 1.25rem; }
h4 { font-size: 1.125rem; }
h5 { font-size: 1rem; }
h6 { font-size: 0.875rem; }

a {
  color: var(--primary);
  text-decoration: none;
  transition: color 0.2s;
}
a:hover {
  text-decoration: underline;
}

button {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  height: 2.5rem;
  padding: 0 1rem;
  background: var(--primary);
  color: var(--primary-foreground);
  font-family: inherit;
  font-size: 0.875rem;
  font-weight: 500;
  border: none;
  border-radius: 0.375rem;
  cursor: pointer;
  transition: opacity 0.2s;
  white-space: nowrap;
}
button:hover {
  opacity: 0.9;
}
button:active {
  opacity: 0.8;
}
button:focus-visible {
  outline: 2px solid var(--ring);
  outline-offset: 2px;
}

input, textarea {
  background: var(--background);
  color: var(--foreground);
  font-family: inherit;
  font-size: 0.875rem;
  border: 1px solid var(--border);
  border-radius: 0.375rem;
  padding: 0.5rem 0.75rem;
  width: 100%;
  outline: none;
  transition: border-color 0.2s, box-shadow 0.2s;
}
input:focus, textarea:focus {
  border-color: var(--ring);
  box-shadow: 0 0 0 2px color-mix(in srgb, var(--ring) 25%, transparent);
}
input::placeholder, textarea::placeholder {
  color: var(--muted-foreground);
}

table {
  width: 100%;
  border-collapse: collapse;
  border: 1px solid var(--border);
  border-radius: 0.5rem;
  overflow: hidden;
}
th {
  font-weight: 500;
  font-size: 0.75rem;
  color: var(--muted-foreground);
  background: var(--muted);
  text-align: left;
  padding: 0.625rem 1rem;
}
td {
  border-bottom: 1px solid var(--border);
  padding: 0.625rem 1rem;
  font-size: 0.875rem;
}
tr:last-child td {
  border-bottom: none;
}

hr {
  border: none;
  border-top: 1px solid var(--border);
  margin: 1.5rem 0;
}

code {
  background: var(--muted);
  color: var(--accent-foreground);
  padding: 0.15em 0.4em;
  font-size: 0.875em;
  border-radius: 0.25rem;
}

pre {
  background: var(--muted);
  padding: 1rem;
  overflow-x: auto;
  margin: 0.75rem 0;
  border-radius: 0.5rem;
}
pre code {
  background: none;
  padding: 0;
}

/* --- Button Variants --- */

button.ghost {
  background: transparent;
  color: var(--foreground);
}
button.ghost:hover {
  background: var(--muted);
  opacity: 1;
}

button.outline {
  background: transparent;
  border: 1px solid var(--border);
  color: var(--foreground);
}
button.outline:hover {
  background: var(--muted);
  opacity: 1;
}

button.sm {
  height: 2.25rem;
  padding: 0 0.875rem;
  font-size: 0.8125rem;
}

button.xs {
  height: 1.75rem;
  padding: 0 0.625rem;
  font-size: 0.75rem;
}

button.icon {
  width: 2.5rem;
  height: 2.5rem;
  padding: 0;
}
button.icon.sm {
  width: 2rem;
  height: 2rem;
}

/* --- Badge --- */

.badge {
  display: inline-flex;
  align-items: center;
  padding: 0.125rem 0.5rem;
  font-size: 0.7rem;
  font-weight: 500;
  background: var(--muted);
  color: var(--muted-foreground);
  border-radius: 9999px;
}
.badge.success { background: #dcfce7; color: #166534; }
.badge.error   { background: #fee2e2; color: #991b1b; }
.badge.warning { background: #fef3c7; color: #92400e; }
.badge.info    { background: #dbeafe; color: #1e40af; }

@media (prefers-color-scheme: dark) {
  .badge.success { background: #166534; color: #4ade80; }
  .badge.error   { background: #7f1d1d; color: #f87171; }
  .badge.warning { background: #78350f; color: #fbbf24; }
  .badge.info    { background: #1e3a5f; color: #60a5fa; }
}

/* --- Card --- */

.card {
  border: 1px solid var(--border);
  background: var(--card);
  padding: 1.5rem;
  border-radius: 0.5rem;
  box-shadow: 0 1px 2px 0 rgb(0 0 0 / 0.05);
  transition: border-color 0.2s;
}

/* --- Chat Messages (CLI-style) --- */

.chat-msg {
  padding: 0.75rem 1rem;
  max-width: 100%;
  border-radius: 0;
  background: none;
  border: none;
  border-top: 1px solid var(--border);
}
.chat-msg:first-child { border-top: none; }

.chat-msg.user {
  margin-left: 0;
  background: none;
  color: var(--foreground);
}
.chat-msg.ai {
  margin-right: 0;
  background: none;
  color: var(--foreground);
}

/* Role label styling */
.chat-msg.user .msg-meta span:first-child {
  color: var(--primary);
  font-weight: 600;
}
.chat-msg.ai .msg-meta span:first-child {
  color: var(--muted-foreground);
  font-weight: 600;
}

/* --- Sidebar --- */

#sidebar-toggle {
  display: none;
}

.sidebar {
  position: fixed;
  top: 0;
  left: 0;
  width: 16rem;
  height: 100dvh;
  background: var(--card);
  border-right: 1px solid var(--border);
  transform: translateX(-100%);
  transition: transform 0.2s ease;
  z-index: 50;
  overflow-y: auto;
  padding: 1.5rem;
  border-radius: 0 0.75rem 0.75rem 0;
}

#sidebar-toggle:checked ~ .sidebar-container .sidebar {
  transform: translateX(0);
}

.sidebar-container::before {
  content: '';
  position: fixed;
  inset: 0;
  background: rgba(0, 0, 0, 0.4);
  z-index: 40;
  opacity: 0;
  pointer-events: none;
  transition: opacity 0.2s ease;
}
#sidebar-toggle:checked ~ .sidebar-container::before {
  opacity: 1;
  pointer-events: auto;
}

.sidebar nav a {
  display: block;
  padding: 0.5rem 0.75rem;
  color: var(--muted-foreground);
  font-size: 0.875rem;
  font-weight: 500;
  text-decoration: none;
  border-radius: 0.375rem;
  transition: background 0.15s, color 0.15s;
}
.sidebar nav a:hover {
  background: var(--muted);
  color: var(--foreground);
  text-decoration: none;
}

/* --- Utility Classes --- */

.section-heading {
  font-size: 1.125rem;
  font-weight: 600;
  margin-bottom: 1rem;
  letter-spacing: -0.025em;
}

.msg-meta {
  font-size: 0.65rem;
  text-transform: uppercase;
  letter-spacing: 0.05em;
  color: var(--muted-foreground);
}

.notification-unread {
  border-left: 3px solid var(--primary);
}

/* --- Notifications Dropdown --- */

.notifications-dropdown {
  position: absolute;
  top: calc(100% + 0.5rem);
  right: 0;
  z-index: 100;
  width: 360px;
  max-height: 480px;
  background: var(--card);
  border: 1px solid var(--border);
  border-radius: 0.5rem;
  box-shadow: 0 10px 15px -3px rgb(0 0 0 / 0.1), 0 4px 6px -4px rgb(0 0 0 / 0.1);
  display: flex;
  flex-direction: column;
  animation: ctx-menu-in 0.12s ease-out;
}

.notifications-dropdown-header {
  padding: 0.75rem 1rem;
  border-bottom: 1px solid var(--border);
  flex-shrink: 0;
}

.notifications-dropdown-list {
  overflow-y: auto;
  flex: 1;
  min-height: 0;
}

.notifications-dropdown-list .card {
  border-radius: 0;
  border-left: none;
  border-right: none;
  border-top: none;
  box-shadow: none;
  padding: 0.75rem 1rem;
}
.notifications-dropdown-list .card:last-child {
  border-bottom: none;
}

.notifications-dropdown-list .notification-unread {
  border-left: 3px solid var(--primary);
}

.notifications-dropdown-footer {
  border-top: 1px solid var(--border);
  padding: 0.5rem;
  flex-shrink: 0;
}

@media (max-width: 639px) {
  .notifications-dropdown {
    width: calc(100vw - 1rem);
    right: -0.5rem;
  }
}

/* --- Notification Card Submenu --- */

.notification-submenu {
  position: relative;
}

.notification-submenu > summary {
  cursor: pointer;
  list-style: none;
  display: inline-flex;
  align-items: center;
  gap: 0.375rem;
  height: 1.75rem;
  padding: 0 0.625rem;
  font-size: 0.75rem;
  font-weight: 500;
  background: transparent;
  border: 1px solid var(--border);
  color: var(--foreground);
  border-radius: 0.375rem;
  transition: background 0.15s;
}
.notification-submenu > summary:hover {
  background: var(--muted);
}
.notification-submenu > summary::-webkit-details-marker {
  display: none;
}
.notification-submenu > summary::after {
  content: '';
  display: inline-block;
  width: 0;
  height: 0;
  border-left: 4px solid transparent;
  border-right: 4px solid transparent;
  border-top: 4px solid var(--muted-foreground);
  margin-left: 0.25rem;
}

.notification-submenu-panel {
  position: absolute;
  bottom: calc(100% + 4px);
  left: 0;
  z-index: 50;
  min-width: 220px;
  max-width: 320px;
  background: var(--card);
  border: 1px solid var(--border);
  border-radius: 0.5rem;
  box-shadow: 0 4px 6px -1px rgb(0 0 0 / 0.1), 0 2px 4px -2px rgb(0 0 0 / 0.1);
  padding: 0.25rem;
  animation: ctx-menu-in 0.12s ease-out;
}

.notification-submenu-item {
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  gap: 0.125rem;
  width: 100%;
  height: auto;
  padding: 0.375rem 0.625rem;
  background: transparent;
  color: var(--foreground);
  font-size: 0.8125rem;
  font-weight: 400;
  border: none;
  border-radius: 0.25rem;
  cursor: pointer;
  text-align: left;
}
.notification-submenu-item:hover {
  background: var(--muted);
  opacity: 1;
}

.notification-submenu-new {
  color: var(--primary);
}

.notification-submenu-section {
  font-weight: 500;
}

.notification-submenu-divider {
  height: 1px;
  background: var(--border);
  margin: 0.25rem 0;
}

.nav-trigger {
  cursor: pointer;
  display: inline-flex;
  align-items: center;
  justify-content: center;
  width: 2.5rem;
  height: 2.5rem;
  border-radius: 0.375rem;
  transition: background 0.15s;
}
.nav-trigger:hover {
  background: var(--muted);
}

.notification-badge {
  background: var(--primary);
  color: var(--primary-foreground);
  font-size: 0.6rem;
  padding: 0.1rem 0.35rem;
  border-radius: 9999px;
  font-weight: 600;
}

/* --- Prose Bubble (Markdown in Chat) --- */

.prose-bubble > *:first-child { margin-top: 0; }
.prose-bubble > *:last-child { margin-bottom: 0; }

.prose-bubble p { margin: 0.4em 0; }
.prose-bubble h1, .prose-bubble h2, .prose-bubble h3,
.prose-bubble h4, .prose-bubble h5, .prose-bubble h6 {
  font-weight: 600;
  margin: 0.6em 0 0.3em;
}
.prose-bubble h1 { font-size: 1.25em; }
.prose-bubble h2 { font-size: 1.15em; }
.prose-bubble h3 { font-size: 1.05em; }

.prose-bubble ul, .prose-bubble ol { margin: 0.4em 0; padding-left: 1.4em; }
.prose-bubble ul { list-style: disc; }
.prose-bubble ol { list-style: decimal; }
.prose-bubble li { margin: 0.15em 0; }

.prose-bubble code {
  background: var(--muted);
  color: var(--accent-foreground);
  padding: 0.15em 0.35em;
  font-size: 0.875em;
  border-radius: 0.25rem;
}
.prose-bubble pre {
  background: var(--muted);
  padding: 0.75em;
  overflow-x: auto;
  margin: 0.5em 0;
  border-radius: 0.375rem;
}
.prose-bubble pre code {
  background: none;
  padding: 0;
  font-size: 0.85em;
}

.prose-bubble blockquote {
  border-left: 3px solid var(--border);
  padding-left: 0.75em;
  margin: 0.4em 0;
  color: var(--muted-foreground);
}

.prose-bubble table { border-collapse: collapse; margin: 0.5em 0; width: 100%; }
.prose-bubble th, .prose-bubble td {
  border: 1px solid var(--border);
  padding: 0.3em 0.6em;
  text-align: left;
}
.prose-bubble th { font-weight: 600; background: var(--muted); }

.prose-bubble a { color: var(--primary); text-decoration: underline; }
.prose-bubble a:hover { opacity: 0.8; }

.prose-bubble hr { border: none; border-top: 1px solid var(--border); margin: 0.6em 0; }

/* --- Agent Steps (streaming progress) --- */

.agent-steps {
  display: flex;
  flex-direction: column;
  gap: 0.125rem;
  margin-bottom: 0.5rem;
}
.agent-step {
  display: flex;
  align-items: center;
  gap: 0.5rem;
  font-size: 0.75rem;
  color: var(--muted-foreground);
}
.agent-step-icon {
  width: 0.5rem;
  height: 0.5rem;
  border-radius: 50%;
  background: var(--muted-foreground);
  flex-shrink: 0;
}
/* Pulse the active (last) step */
.agent-step:last-child .agent-step-icon {
  background: var(--primary);
  animation: step-pulse 1.5s ease-in-out infinite;
}
@keyframes step-pulse {
  0%, 100% { opacity: 1; }
  50% { opacity: 0.3; }
}

/* --- Custom Scrollbar --- */

.messages::-webkit-scrollbar { width: 6px; }
.messages::-webkit-scrollbar-track { background: transparent; }
.messages::-webkit-scrollbar-thumb { background: var(--border); border-radius: 3px; }
.messages::-webkit-scrollbar-thumb:hover { background: var(--muted-foreground); }

/* --- Reduced Motion --- */

@media (prefers-reduced-motion: reduce) {
  *, *::before, *::after {
    transition: none !important;
    animation: none !important;
  }
}

/* --- Chat Footer --- */

.chat-footer {
  display: flex;
  flex-direction: column;
  border-top: 1px solid var(--border);
  background: var(--card);
  min-height: 2.5rem;
  max-height: calc(100dvh - 80px);
  overflow: hidden;
  flex-shrink: 0;
}

.chat-footer-handle {
  height: 6px;
  cursor: ns-resize;
  background: transparent;
  position: relative;
  flex-shrink: 0;
}
.chat-footer-handle::after {
  content: '';
  position: absolute;
  left: 50%;
  top: 50%;
  transform: translate(-50%, -50%);
  width: 32px;
  height: 3px;
  border-radius: 2px;
  background: var(--border);
  transition: background 0.15s;
}
.chat-footer-handle:hover::after {
  background: var(--muted-foreground);
}

.chat-tab-bar {
  display: flex;
  align-items: center;
  gap: 0;
  border-bottom: 1px solid var(--border);
  padding: 0 0.25rem;
  flex-shrink: 0;
  overflow-x: auto;
  min-height: 2rem;
}

.chat-tabs {
  display: flex;
  align-items: center;
  gap: 0;
  overflow-x: auto;
  flex: 1;
  min-width: 0;
}

.chat-tab {
  display: inline-flex;
  align-items: center;
  gap: 0.375rem;
  padding: 0.25rem 0.5rem;
  font-size: 0.75rem;
  font-weight: 500;
  color: var(--muted-foreground);
  background: transparent;
  border: none;
  border-bottom: 2px solid transparent;
  border-radius: 0;
  height: 2rem;
  cursor: pointer;
  white-space: nowrap;
  transition: color 0.15s, border-color 0.15s;
}
.chat-tab:hover {
  color: var(--foreground);
  opacity: 1;
}
.chat-tab.active {
  color: var(--foreground);
  border-bottom-color: var(--primary);
}

.chat-tab-title {
  max-width: 120px;
  overflow: hidden;
  text-overflow: ellipsis;
}

.chat-tab-close {
  font-size: 0.875rem;
  line-height: 1;
  opacity: 0.5;
  cursor: pointer;
  padding: 0 0.125rem;
}
.chat-tab-close:hover {
  opacity: 1;
}

.chat-tab-new {
  flex-shrink: 0;
  width: 2rem;
  height: 2rem;
  font-size: 1rem;
  padding: 0;
}

/* Two-level tab bars */
.chat-tab-bar.canvas-level {
  background: var(--muted);
  border-bottom: none;
}
.chat-tab-bar.conversation-level {
  min-height: 1.75rem;
  padding-left: 0.5rem;
}
.chat-tab-bar.conversation-level .chat-tab {
  font-size: 0.6875rem;
  height: 1.75rem;
}
.chat-tab-bar.conversation-level .chat-tab-new {
  width: 1.75rem;
  height: 1.75rem;
  font-size: 0.875rem;
}

.chat-panels {
  flex: 1;
  position: relative;
  overflow: hidden;
  min-height: 0;
}

.chat-panel {
  display: none;
  position: absolute;
  inset: 0;
  flex-direction: column;
  overflow: hidden;
}
.chat-panel.active {
  display: flex;
}

.chat-panel-inner {
  display: flex;
  flex-direction: column;
  height: 100%;
  overflow: hidden;
}

.chat-panel-messages {
  flex: 1;
  overflow-y: auto;
  padding: 0.75rem;
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
}
.chat-panel-messages::-webkit-scrollbar { width: 5px; }
.chat-panel-messages::-webkit-scrollbar-track { background: transparent; }
.chat-panel-messages::-webkit-scrollbar-thumb { background: var(--border); border-radius: 3px; }

/* Compact message styles inside footer */
.chat-panel-messages .chat-msg {
  padding: 0.5rem 0.75rem;
  font-size: 0.8125rem;
  max-width: 100%;
}
.chat-panel-messages .msg-meta {
  font-size: 0.6rem;
}
.chat-panel-messages .prose-bubble p {
  margin: 0.25em 0;
}

.chat-panel-form {
  display: flex;
  align-items: flex-end;
  gap: 0.5rem;
  padding: 0.5rem 0.75rem;
  border-top: 1px solid var(--border);
  flex-shrink: 0;
}

.chat-panel-input {
  flex: 1;
  min-width: 0;
  resize: none;
  font-size: 0.8125rem;
  padding: 0.375rem 0.625rem;
  max-height: 120px;
}

.chat-panel-loading {
  padding: 2rem;
  text-align: center;
  color: var(--muted-foreground);
  font-size: 0.8125rem;
}

/* --- Conversation Canvas --- */

.conversation-canvas {
  display: none;
  position: absolute;
  inset: 0;
  overflow: hidden;
  background: var(--background);
}
.conversation-canvas.active {
  display: block;
}

/* Dot grid background */
.canvas-dot-grid {
  position: absolute;
  inset: 0;
  background-image: radial-gradient(circle, var(--border) 1px, transparent 1px);
  background-size: 24px 24px;
  pointer-events: none;
  z-index: 0;
}

/* The pannable/zoomable world */
.canvas-world {
  position: absolute;
  top: 0;
  left: 0;
  transform-origin: 0 0;
  will-change: transform;
  z-index: 1;
}

/* Individual canvas component */
.canvas-component {
  position: absolute;
  background: var(--card);
  border: 1px solid var(--border);
  border-radius: 0.5rem;
  box-shadow: 0 1px 3px 0 rgb(0 0 0 / 0.1), 0 1px 2px -1px rgb(0 0 0 / 0.1);
  cursor: grab;
  user-select: none;
  transition: box-shadow 0.15s, border-color 0.15s;
}
.canvas-component:hover {
  border-color: var(--primary);
  box-shadow: 0 4px 6px -1px rgb(0 0 0 / 0.1), 0 2px 4px -2px rgb(0 0 0 / 0.1);
}
.canvas-component.dragging {
  cursor: grabbing;
  box-shadow: 0 10px 15px -3px rgb(0 0 0 / 0.2), 0 4px 6px -4px rgb(0 0 0 / 0.2);
  border-color: var(--primary);
  z-index: 9999 !important;
  opacity: 0.95;
}
.canvas-component.canvas-component-highlight {
  border-color: var(--primary);
  box-shadow: 0 0 0 3px color-mix(in srgb, var(--primary) 30%, transparent), 0 4px 6px -1px rgb(0 0 0 / 0.1);
  animation: comp-highlight-pulse 0.6s ease-in-out 2;
}
@keyframes comp-highlight-pulse {
  0%, 100% { box-shadow: 0 0 0 3px color-mix(in srgb, var(--primary) 30%, transparent); }
  50% { box-shadow: 0 0 0 6px color-mix(in srgb, var(--primary) 15%, transparent); }
}

.canvas-component-content {
  padding: 1rem;
  font-size: 0.875rem;
  overflow: hidden;
}
.canvas-component-content[contenteditable="true"] {
  outline: 2px solid var(--ring);
  outline-offset: -2px;
  border-radius: 0.375rem;
  cursor: text;
  overflow: auto;
}
.canvas-component-content > *:first-child { margin-top: 0; }
.canvas-component-content > *:last-child { margin-bottom: 0; }

/* Canvas context menu */
.canvas-context-menu {
  position: fixed;
  z-index: 10000;
  min-width: 180px;
  background: var(--card);
  border: 1px solid var(--border);
  border-radius: 0.5rem;
  box-shadow: 0 4px 6px -1px rgb(0 0 0 / 0.1), 0 2px 4px -2px rgb(0 0 0 / 0.1);
  padding: 0.25rem;
  animation: ctx-menu-in 0.12s ease-out;
}

@keyframes ctx-menu-in {
  from { opacity: 0; transform: scale(0.95); }
  to { opacity: 1; transform: scale(1); }
}

.canvas-context-menu-item {
  display: flex;
  align-items: center;
  gap: 0.5rem;
  width: 100%;
  height: auto;
  padding: 0.375rem 0.625rem;
  background: transparent;
  color: var(--foreground);
  font-size: 0.8125rem;
  font-weight: 400;
  border: none;
  border-radius: 0.25rem;
  cursor: pointer;
  text-align: left;
  justify-content: flex-start;
}
.canvas-context-menu-item:hover {
  background: var(--muted);
  opacity: 1;
}

.canvas-context-menu-item.destructive {
  color: #ef4444;
}
.canvas-context-menu-item.destructive:hover {
  background: #fef2f2;
}
@media (prefers-color-scheme: dark) {
  .canvas-context-menu-item.destructive:hover {
    background: #451a1a;
  }
}

.canvas-context-menu-separator {
  height: 1px;
  background: var(--border);
  margin: 0.25rem 0;
}

/* Zoom indicator */
.canvas-zoom-indicator {
  position: absolute;
  bottom: 0.75rem;
  right: 0.75rem;
  background: var(--card);
  border: 1px solid var(--border);
  border-radius: 0.375rem;
  padding: 0.25rem 0.5rem;
  font-size: 0.7rem;
  color: var(--muted-foreground);
  z-index: 10;
  pointer-events: none;
  display: flex;
  align-items: center;
  gap: 0.25rem;
}
.canvas-scroll-lock-btn {
  all: unset;
  cursor: pointer;
  padding: 0.125rem;
  border-radius: 0.25rem;
  line-height: 1;
  font-size: 0.8rem;
  pointer-events: auto;
}
.canvas-scroll-lock-btn:hover {
  background: var(--muted);
}

/* --- Scroll Lock Mode --- */
.conversation-canvas.scroll-locked {
  overflow-y: auto;
}
.conversation-canvas.scroll-locked .canvas-dot-grid {
  display: none;
}

/* --- Heartbeat Grid --- */

@keyframes heartbeat-pulse {
  0%, 100% { opacity: 1; transform: scale(1); }
  50% { opacity: 0.7; transform: scale(1.15); }
}

.hb-grid table { width: 100%; }
.hb-grid th, .hb-grid td { vertical-align: middle; }

.hb-cell {
  display: inline-block;
  width: 16px;
  height: 16px;
  border-radius: 3px;
  background: var(--muted);
  flex-shrink: 0;
  cursor: default;
  transition: transform 0.1s ease;
}

.hb-cell:hover {
  transform: scale(1.3);
  z-index: 1;
}

.hb-cell.success { background: #10b981; }
.hb-cell.skipped { background: var(--muted-foreground); opacity: 0.4; }
.hb-cell.error { background: #ef4444; }

@media (prefers-color-scheme: dark) {
  .hb-cell.skipped { background: #52525b; opacity: 1; }
}

/* --- Mobile --- */

@media (max-width: 639px) {
  main.page-content { padding: 0.75rem; }
  .card { padding: 1rem; }
  button { min-height: 2.75rem; }
  button.icon { width: 2.75rem; height: 2.75rem; }
  .chat-msg { max-width: 100%; }
  .sidebar nav a { min-height: 2.75rem; display: flex; align-items: center; }

  .chat-tab { padding: 0.25rem 0.375rem; font-size: 0.6875rem; }
  .chat-tab-title { max-width: 80px; }
  .chat-panel-messages .chat-msg { max-width: 100%; }
}

/* ============================================
   NED — shadcn/ui Design System
   Clean · Minimal · Terminal Green Accent · Light/Dark
   ============================================ */

/* --- Design Tokens (Light Mode) --- */
:root {
  --background: #d5d8dc;
  --foreground: #1a1a1a;
  --card: #c8ccd0;
  --card-foreground: #1a1a1a;
  --muted: #bcc1c6;
  --muted-foreground: #4a5568;
  --accent: #f0fdf4;
  --accent-foreground: #14532d;
  --primary: #15803d;
  --primary-foreground: #ffffff;
  --border: #e2e8f0;
  --ring: #15803d;

  /* Backward-compat aliases */
  --ned-bg: var(--background);
  --ned-fg: var(--foreground);
  --ned-muted: var(--muted);
  --ned-muted-fg: var(--muted-foreground);
  --ned-accent: var(--primary);
  --ned-accent-fg: var(--primary-foreground);
  --ned-border: var(--border);
  --ned-card: var(--card);
}

/* --- Dark Mode --- */
@media (prefers-color-scheme: dark) {
  :root {
    --background: #09090b;
    --foreground: #fafafa;
    --card: #18181b;
    --card-foreground: #fafafa;
    --muted: #27272a;
    --muted-foreground: #a1a1aa;
    --accent: #27272a;
    --accent-foreground: #22c55e;
    --primary: #16a34a;
    --primary-foreground: #ffffff;
    --border: #27272a;
    --ring: #16a34a;
  }
}

/* --- Element Base Styles --- */

body {
  background: var(--background);
  color: var(--foreground);
  font-family: 'Inter', system-ui, sans-serif;
  line-height: 1.5;
}

h1, h2, h3, h4, h5, h6 {
  font-weight: 600;
  line-height: 1.2;
  letter-spacing: -0.025em;
}

h1 { font-size: 1.875rem; }
h2 { font-size: 1.5rem; }
h3 { font-size: 1.25rem; }
h4 { font-size: 1.125rem; }
h5 { font-size: 1rem; }
h6 { font-size: 0.875rem; }

a {
  color: var(--primary);
  text-decoration: none;
  transition: color 0.2s;
}
a:hover {
  text-decoration: underline;
}

button {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  height: 2.5rem;
  padding: 0 1rem;
  background: var(--primary);
  color: var(--primary-foreground);
  font-family: inherit;
  font-size: 0.875rem;
  font-weight: 500;
  border: none;
  border-radius: 0.375rem;
  cursor: pointer;
  transition: opacity 0.2s;
  white-space: nowrap;
}
button:hover {
  opacity: 0.9;
}
button:active {
  opacity: 0.8;
}
button:focus-visible {
  outline: 2px solid var(--ring);
  outline-offset: 2px;
}

input, textarea {
  background: var(--background);
  color: var(--foreground);
  font-family: inherit;
  font-size: 0.875rem;
  border: 1px solid var(--border);
  border-radius: 0.375rem;
  padding: 0.5rem 0.75rem;
  width: 100%;
  outline: none;
  transition: border-color 0.2s, box-shadow 0.2s;
}
input:focus, textarea:focus {
  border-color: var(--ring);
  box-shadow: 0 0 0 2px color-mix(in srgb, var(--ring) 25%, transparent);
}
input::placeholder, textarea::placeholder {
  color: var(--muted-foreground);
}

table {
  width: 100%;
  border-collapse: collapse;
  border: 1px solid var(--border);
  border-radius: 0.5rem;
  overflow: hidden;
}
th {
  font-weight: 500;
  font-size: 0.75rem;
  color: var(--muted-foreground);
  background: var(--muted);
  text-align: left;
  padding: 0.625rem 1rem;
}
td {
  border-bottom: 1px solid var(--border);
  padding: 0.625rem 1rem;
  font-size: 0.875rem;
}
tr:last-child td {
  border-bottom: none;
}

hr {
  border: none;
  border-top: 1px solid var(--border);
  margin: 1.5rem 0;
}

code {
  background: var(--muted);
  color: var(--accent-foreground);
  padding: 0.15em 0.4em;
  font-size: 0.875em;
  border-radius: 0.25rem;
}

pre {
  background: var(--muted);
  padding: 1rem;
  overflow-x: auto;
  margin: 0.75rem 0;
  border-radius: 0.5rem;
}
pre code {
  background: none;
  padding: 0;
}

/* --- Button Variants --- */

button.ghost {
  background: transparent;
  color: var(--foreground);
}
button.ghost:hover {
  background: var(--muted);
  opacity: 1;
}

button.outline {
  background: transparent;
  border: 1px solid var(--border);
  color: var(--foreground);
}
button.outline:hover {
  background: var(--muted);
  opacity: 1;
}

button.sm {
  height: 2.25rem;
  padding: 0 0.875rem;
  font-size: 0.8125rem;
}

button.xs {
  height: 1.75rem;
  padding: 0 0.625rem;
  font-size: 0.75rem;
}

button.icon {
  width: 2.5rem;
  height: 2.5rem;
  padding: 0;
}
button.icon.sm {
  width: 2rem;
  height: 2rem;
}

/* --- Badge --- */

.badge {
  display: inline-flex;
  align-items: center;
  padding: 0.125rem 0.5rem;
  font-size: 0.7rem;
  font-weight: 500;
  background: var(--muted);
  color: var(--muted-foreground);
  border-radius: 9999px;
}
.badge.success { background: #dcfce7; color: #166534; }
.badge.error   { background: #fee2e2; color: #991b1b; }
.badge.warning { background: #fef3c7; color: #92400e; }
.badge.info    { background: #dbeafe; color: #1e40af; }

@media (prefers-color-scheme: dark) {
  .badge.success { background: #166534; color: #4ade80; }
  .badge.error   { background: #7f1d1d; color: #f87171; }
  .badge.warning { background: #78350f; color: #fbbf24; }
  .badge.info    { background: #1e3a5f; color: #60a5fa; }
}

/* --- Card --- */

.card {
  border: 1px solid var(--border);
  background: var(--card);
  padding: 1.5rem;
  border-radius: 0.5rem;
  box-shadow: 0 1px 2px 0 rgb(0 0 0 / 0.05);
  transition: border-color 0.2s;
}

/* --- Chat Messages (CLI-style) --- */

.chat-msg {
  padding: 0.75rem 1rem;
  max-width: 100%;
  border-radius: 0;
  background: none;
  border: none;
  border-top: 1px solid var(--border);
}
.chat-msg:first-child { border-top: none; }

.chat-msg.user {
  margin-left: 0;
  background: none;
  color: var(--foreground);
}
.chat-msg.ai {
  margin-right: 0;
  background: none;
  color: var(--foreground);
}

/* Role label styling */
.chat-msg.user .msg-meta span:first-child {
  color: var(--primary);
  font-weight: 600;
}
.chat-msg.ai .msg-meta span:first-child {
  color: var(--muted-foreground);
  font-weight: 600;
}

/* --- Sidebar --- */

#sidebar-toggle {
  display: none;
}

.sidebar {
  position: fixed;
  top: 0;
  left: 0;
  width: 16rem;
  height: 100dvh;
  background: var(--card);
  border-right: 1px solid var(--border);
  transform: translateX(-100%);
  transition: transform 0.2s ease;
  z-index: 50;
  overflow-y: auto;
  padding: 1.5rem;
  border-radius: 0 0.75rem 0.75rem 0;
}

#sidebar-toggle:checked ~ .sidebar-container .sidebar {
  transform: translateX(0);
}

.sidebar-container::before {
  content: '';
  position: fixed;
  inset: 0;
  background: rgba(0, 0, 0, 0.4);
  z-index: 40;
  opacity: 0;
  pointer-events: none;
  transition: opacity 0.2s ease;
}
#sidebar-toggle:checked ~ .sidebar-container::before {
  opacity: 1;
  pointer-events: auto;
}

.sidebar nav a {
  display: block;
  padding: 0.5rem 0.75rem;
  color: var(--muted-foreground);
  font-size: 0.875rem;
  font-weight: 500;
  text-decoration: none;
  border-radius: 0.375rem;
  transition: background 0.15s, color 0.15s;
}
.sidebar nav a:hover {
  background: var(--muted);
  color: var(--foreground);
  text-decoration: none;
}

/* --- Utility Classes --- */

.section-heading {
  font-size: 1.125rem;
  font-weight: 600;
  margin-bottom: 1rem;
  letter-spacing: -0.025em;
}

.msg-meta {
  font-size: 0.65rem;
  text-transform: uppercase;
  letter-spacing: 0.05em;
  color: var(--muted-foreground);
}

.notification-unread {
  border-left: 3px solid var(--primary);
}

/* --- Notifications Dropdown --- */

.notifications-dropdown {
  position: absolute;
  top: calc(100% + 0.5rem);
  right: 0;
  z-index: 100;
  width: 360px;
  max-height: 480px;
  background: var(--card);
  border: 1px solid var(--border);
  border-radius: 0.5rem;
  box-shadow: 0 10px 15px -3px rgb(0 0 0 / 0.1), 0 4px 6px -4px rgb(0 0 0 / 0.1);
  display: flex;
  flex-direction: column;
  animation: ctx-menu-in 0.12s ease-out;
}

.notifications-dropdown-header {
  padding: 0.75rem 1rem;
  border-bottom: 1px solid var(--border);
  flex-shrink: 0;
}

.notifications-dropdown-list {
  overflow-y: auto;
  flex: 1;
  min-height: 0;
}

.notifications-dropdown-list .card {
  border-radius: 0;
  border-left: none;
  border-right: none;
  border-top: none;
  box-shadow: none;
  padding: 0.75rem 1rem;
}
.notifications-dropdown-list .card:last-child {
  border-bottom: none;
}

.notifications-dropdown-list .notification-unread {
  border-left: 3px solid var(--primary);
}

.notifications-dropdown-footer {
  border-top: 1px solid var(--border);
  padding: 0.5rem;
  flex-shrink: 0;
}

@media (max-width: 639px) {
  .notifications-dropdown {
    width: calc(100vw - 1rem);
    right: -0.5rem;
  }
}

/* --- Notification Card Submenu --- */

.notification-submenu {
  position: relative;
}

.notification-submenu > summary {
  cursor: pointer;
  list-style: none;
  display: inline-flex;
  align-items: center;
  gap: 0.375rem;
  height: 1.75rem;
  padding: 0 0.625rem;
  font-size: 0.75rem;
  font-weight: 500;
  background: transparent;
  border: 1px solid var(--border);
  color: var(--foreground);
  border-radius: 0.375rem;
  transition: background 0.15s;
}
.notification-submenu > summary:hover {
  background: var(--muted);
}
.notification-submenu > summary::-webkit-details-marker {
  display: none;
}
.notification-submenu > summary::after {
  content: '';
  display: inline-block;
  width: 0;
  height: 0;
  border-left: 4px solid transparent;
  border-right: 4px solid transparent;
  border-top: 4px solid var(--muted-foreground);
  margin-left: 0.25rem;
}

.notification-submenu-panel {
  position: absolute;
  bottom: calc(100% + 4px);
  left: 0;
  z-index: 50;
  min-width: 220px;
  max-width: 320px;
  background: var(--card);
  border: 1px solid var(--border);
  border-radius: 0.5rem;
  box-shadow: 0 4px 6px -1px rgb(0 0 0 / 0.1), 0 2px 4px -2px rgb(0 0 0 / 0.1);
  padding: 0.25rem;
  animation: ctx-menu-in 0.12s ease-out;
}

.notification-submenu-item {
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  gap: 0.125rem;
  width: 100%;
  height: auto;
  padding: 0.375rem 0.625rem;
  background: transparent;
  color: var(--foreground);
  font-size: 0.8125rem;
  font-weight: 400;
  border: none;
  border-radius: 0.25rem;
  cursor: pointer;
  text-align: left;
}
.notification-submenu-item:hover {
  background: var(--muted);
  opacity: 1;
}

.notification-submenu-new {
  color: var(--primary);
}

.notification-submenu-section {
  font-weight: 500;
}

.notification-submenu-divider {
  height: 1px;
  background: var(--border);
  margin: 0.25rem 0;
}

.nav-trigger {
  cursor: pointer;
  display: inline-flex;
  align-items: center;
  justify-content: center;
  width: 2.5rem;
  height: 2.5rem;
  border-radius: 0.375rem;
  transition: background 0.15s;
}
.nav-trigger:hover {
  background: var(--muted);
}

.notification-badge {
  background: var(--primary);
  color: var(--primary-foreground);
  font-size: 0.6rem;
  padding: 0.1rem 0.35rem;
  border-radius: 9999px;
  font-weight: 600;
}

/* --- Prose Bubble (Markdown in Chat) --- */

.prose-bubble > *:first-child { margin-top: 0; }
.prose-bubble > *:last-child { margin-bottom: 0; }

.prose-bubble p { margin: 0.4em 0; }
.prose-bubble h1, .prose-bubble h2, .prose-bubble h3,
.prose-bubble h4, .prose-bubble h5, .prose-bubble h6 {
  font-weight: 600;
  margin: 0.6em 0 0.3em;
}
.prose-bubble h1 { font-size: 1.25em; }
.prose-bubble h2 { font-size: 1.15em; }
.prose-bubble h3 { font-size: 1.05em; }

.prose-bubble ul, .prose-bubble ol { margin: 0.4em 0; padding-left: 1.4em; }
.prose-bubble ul { list-style: disc; }
.prose-bubble ol { list-style: decimal; }
.prose-bubble li { margin: 0.15em 0; }

.prose-bubble code {
  background: var(--muted);
  color: var(--accent-foreground);
  padding: 0.15em 0.35em;
  font-size: 0.875em;
  border-radius: 0.25rem;
}
.prose-bubble pre {
  background: var(--muted);
  padding: 0.75em;
  overflow-x: auto;
  margin: 0.5em 0;
  border-radius: 0.375rem;
}
.prose-bubble pre code {
  background: none;
  padding: 0;
  font-size: 0.85em;
}

.prose-bubble blockquote {
  border-left: 3px solid var(--border);
  padding-left: 0.75em;
  margin: 0.4em 0;
  color: var(--muted-foreground);
}

.prose-bubble table { border-collapse: collapse; margin: 0.5em 0; width: 100%; }
.prose-bubble th, .prose-bubble td {
  border: 1px solid var(--border);
  padding: 0.3em 0.6em;
  text-align: left;
}
.prose-bubble th { font-weight: 600; background: var(--muted); }

.prose-bubble a { color: var(--primary); text-decoration: underline; }
.prose-bubble a:hover { opacity: 0.8; }

.prose-bubble hr { border: none; border-top: 1px solid var(--border); margin: 0.6em 0; }

/* --- Agent Steps (streaming progress) --- */

.agent-steps {
  display: flex;
  flex-direction: column;
  gap: 0.125rem;
  margin-bottom: 0.5rem;
}
.agent-step {
  display: flex;
  align-items: center;
  gap: 0.5rem;
  font-size: 0.75rem;
  color: var(--muted-foreground);
}
.agent-step-icon {
  width: 0.5rem;
  height: 0.5rem;
  border-radius: 50%;
  background: var(--muted-foreground);
  flex-shrink: 0;
}
/* Pulse the active (last) step */
.agent-step:last-child .agent-step-icon {
  background: var(--primary);
  animation: step-pulse 1.5s ease-in-out infinite;
}
@keyframes step-pulse {
  0%, 100% { opacity: 1; }
  50% { opacity: 0.3; }
}

/* --- Custom Scrollbar --- */

.messages::-webkit-scrollbar { width: 6px; }
.messages::-webkit-scrollbar-track { background: transparent; }
.messages::-webkit-scrollbar-thumb { background: var(--border); border-radius: 3px; }
.messages::-webkit-scrollbar-thumb:hover { background: var(--muted-foreground); }

/* --- Reduced Motion --- */

@media (prefers-reduced-motion: reduce) {
  *, *::before, *::after {
    transition: none !important;
    animation: none !important;
  }
}

/* --- Chat Footer --- */

.chat-footer {
  display: flex;
  flex-direction: column;
  border-top: 1px solid var(--border);
  background: var(--card);
  min-height: 2.5rem;
  max-height: calc(100dvh - 80px);
  overflow: hidden;
  flex-shrink: 0;
}

.chat-footer-handle {
  height: 6px;
  cursor: ns-resize;
  background: transparent;
  position: relative;
  flex-shrink: 0;
}
.chat-footer-handle::after {
  content: '';
  position: absolute;
  left: 50%;
  top: 50%;
  transform: translate(-50%, -50%);
  width: 32px;
  height: 3px;
  border-radius: 2px;
  background: var(--border);
  transition: background 0.15s;
}
.chat-footer-handle:hover::after {
  background: var(--muted-foreground);
}

.chat-tab-bar {
  display: flex;
  align-items: center;
  gap: 0;
  border-bottom: 1px solid var(--border);
  padding: 0 0.25rem;
  flex-shrink: 0;
  overflow-x: auto;
  min-height: 2rem;
}

.chat-tabs {
  display: flex;
  align-items: center;
  gap: 0;
  overflow-x: auto;
  flex: 1;
  min-width: 0;
}

.chat-tab {
  display: inline-flex;
  align-items: center;
  gap: 0.375rem;
  padding: 0.25rem 0.5rem;
  font-size: 0.75rem;
  font-weight: 500;
  color: var(--muted-foreground);
  background: transparent;
  border: none;
  border-bottom: 2px solid transparent;
  border-radius: 0;
  height: 2rem;
  cursor: pointer;
  white-space: nowrap;
  transition: color 0.15s, border-color 0.15s;
}
.chat-tab:hover {
  color: var(--foreground);
  opacity: 1;
}
.chat-tab.active {
  color: var(--foreground);
  border-bottom-color: var(--primary);
}

.chat-tab-title {
  max-width: 120px;
  overflow: hidden;
  text-overflow: ellipsis;
}

.chat-tab-close {
  font-size: 0.875rem;
  line-height: 1;
  opacity: 0.5;
  cursor: pointer;
  padding: 0 0.125rem;
}
.chat-tab-close:hover {
  opacity: 1;
}

.chat-tab-new {
  flex-shrink: 0;
  width: 2rem;
  height: 2rem;
  font-size: 1rem;
  padding: 0;
}

/* Two-level tab bars */
.chat-tab-bar.canvas-level {
  background: var(--muted);
  border-bottom: none;
}
.chat-tab-bar.conversation-level {
  min-height: 1.75rem;
  padding-left: 0.5rem;
}
.chat-tab-bar.conversation-level .chat-tab {
  font-size: 0.6875rem;
  height: 1.75rem;
}
.chat-tab-bar.conversation-level .chat-tab-new {
  width: 1.75rem;
  height: 1.75rem;
  font-size: 0.875rem;
}

.chat-panels {
  flex: 1;
  position: relative;
  overflow: hidden;
  min-height: 0;
}

.chat-panel {
  display: none;
  position: absolute;
  inset: 0;
  flex-direction: column;
  overflow: hidden;
}
.chat-panel.active {
  display: flex;
}

.chat-panel-inner {
  display: flex;
  flex-direction: column;
  height: 100%;
  overflow: hidden;
}

.chat-panel-messages {
  flex: 1;
  overflow-y: auto;
  padding: 0.75rem;
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
}
.chat-panel-messages::-webkit-scrollbar { width: 5px; }
.chat-panel-messages::-webkit-scrollbar-track { background: transparent; }
.chat-panel-messages::-webkit-scrollbar-thumb { background: var(--border); border-radius: 3px; }

/* Compact message styles inside footer */
.chat-panel-messages .chat-msg {
  padding: 0.5rem 0.75rem;
  font-size: 0.8125rem;
  max-width: 100%;
}
.chat-panel-messages .msg-meta {
  font-size: 0.6rem;
}
.chat-panel-messages .prose-bubble p {
  margin: 0.25em 0;
}

.chat-panel-form {
  display: flex;
  align-items: flex-end;
  gap: 0.5rem;
  padding: 0.5rem 0.75rem;
  border-top: 1px solid var(--border);
  flex-shrink: 0;
}

.chat-panel-input {
  flex: 1;
  min-width: 0;
  resize: none;
  font-size: 0.8125rem;
  padding: 0.375rem 0.625rem;
  max-height: 120px;
}

.chat-panel-loading {
  padding: 2rem;
  text-align: center;
  color: var(--muted-foreground);
  font-size: 0.8125rem;
}

/* --- Conversation Canvas --- */

.conversation-canvas {
  display: none;
  position: absolute;
  inset: 0;
  overflow: hidden;
  background: var(--background);
}
.conversation-canvas.active {
  display: block;
}

/* Dot grid background */
.canvas-dot-grid {
  position: absolute;
  inset: 0;
  background-image: radial-gradient(circle, var(--border) 1px, transparent 1px);
  background-size: 24px 24px;
  pointer-events: none;
  z-index: 0;
}

/* The pannable/zoomable world */
.canvas-world {
  position: absolute;
  top: 0;
  left: 0;
  transform-origin: 0 0;
  will-change: transform;
  z-index: 1;
}

/* Individual canvas component */
.canvas-component {
  position: absolute;
  background: var(--card);
  border: 1px solid var(--border);
  border-radius: 0.5rem;
  box-shadow: 0 1px 3px 0 rgb(0 0 0 / 0.1), 0 1px 2px -1px rgb(0 0 0 / 0.1);
  cursor: grab;
  user-select: none;
  transition: box-shadow 0.15s, border-color 0.15s;
}
.canvas-component:hover {
  border-color: var(--primary);
  box-shadow: 0 4px 6px -1px rgb(0 0 0 / 0.1), 0 2px 4px -2px rgb(0 0 0 / 0.1);
}
.canvas-component.dragging {
  cursor: grabbing;
  box-shadow: 0 10px 15px -3px rgb(0 0 0 / 0.2), 0 4px 6px -4px rgb(0 0 0 / 0.2);
  border-color: var(--primary);
  z-index: 9999 !important;
  opacity: 0.95;
}
.canvas-component.canvas-component-highlight {
  border-color: var(--primary);
  box-shadow: 0 0 0 3px color-mix(in srgb, var(--primary) 30%, transparent), 0 4px 6px -1px rgb(0 0 0 / 0.1);
  animation: comp-highlight-pulse 0.6s ease-in-out 2;
}
@keyframes comp-highlight-pulse {
  0%, 100% { box-shadow: 0 0 0 3px color-mix(in srgb, var(--primary) 30%, transparent); }
  50% { box-shadow: 0 0 0 6px color-mix(in srgb, var(--primary) 15%, transparent); }
}

.canvas-component-content {
  padding: 1rem;
  font-size: 0.875rem;
  overflow: hidden;
}
.canvas-component-content[contenteditable="true"] {
  outline: 2px solid var(--ring);
  outline-offset: -2px;
  border-radius: 0.375rem;
  cursor: text;
  overflow: auto;
}
.canvas-component-content > *:first-child { margin-top: 0; }
.canvas-component-content > *:last-child { margin-bottom: 0; }

/* Canvas context menu */
.canvas-context-menu {
  position: fixed;
  z-index: 10000;
  min-width: 180px;
  background: var(--card);
  border: 1px solid var(--border);
  border-radius: 0.5rem;
  box-shadow: 0 4px 6px -1px rgb(0 0 0 / 0.1), 0 2px 4px -2px rgb(0 0 0 / 0.1);
  padding: 0.25rem;
  animation: ctx-menu-in 0.12s ease-out;
}

@keyframes ctx-menu-in {
  from { opacity: 0; transform: scale(0.95); }
  to { opacity: 1; transform: scale(1); }
}

.canvas-context-menu-item {
  display: flex;
  align-items: center;
  gap: 0.5rem;
  width: 100%;
  height: auto;
  padding: 0.375rem 0.625rem;
  background: transparent;
  color: var(--foreground);
  font-size: 0.8125rem;
  font-weight: 400;
  border: none;
  border-radius: 0.25rem;
  cursor: pointer;
  text-align: left;
  justify-content: flex-start;
}
.canvas-context-menu-item:hover {
  background: var(--muted);
  opacity: 1;
}

.canvas-context-menu-item.destructive {
  color: #ef4444;
}
.canvas-context-menu-item.destructive:hover {
  background: #fef2f2;
}
@media (prefers-color-scheme: dark) {
  .canvas-context-menu-item.destructive:hover {
    background: #451a1a;
  }
}

.canvas-context-menu-separator {
  height: 1px;
  background: var(--border);
  margin: 0.25rem 0;
}

/* Zoom indicator */
.canvas-zoom-indicator {
  position: absolute;
  bottom: 0.75rem;
  right: 0.75rem;
  background: var(--card);
  border: 1px solid var(--border);
  border-radius: 0.375rem;
  padding: 0.25rem 0.5rem;
  font-size: 0.7rem;
  color: var(--muted-foreground);
  z-index: 10;
  pointer-events: none;
  display: flex;
  align-items: center;
  gap: 0.25rem;
}
.canvas-scroll-lock-btn {
  all: unset;
  cursor: pointer;
  padding: 0.125rem;
  border-radius: 0.25rem;
  line-height: 1;
  font-size: 0.8rem;
  pointer-events: auto;
}
.canvas-scroll-lock-btn:hover {
  background: var(--muted);
}

/* --- Scroll Lock Mode --- */
.conversation-canvas.scroll-locked {
  overflow-y: auto;
}
.conversation-canvas.scroll-locked .canvas-dot-grid {
  display: none;
}

/* --- Heartbeat Grid --- */

@keyframes heartbeat-pulse {
  0%, 100% { opacity: 1; transform: scale(1); }
  50% { opacity: 0.7; transform: scale(1.15); }
}

.hb-grid table { width: 100%; }
.hb-grid th, .hb-grid td { vertical-align: middle; }

.hb-cell {
  display: inline-block;
  width: 16px;
  height: 16px;
  border-radius: 3px;
  background: var(--muted);
  flex-shrink: 0;
  cursor: default;
  transition: transform 0.1s ease;
}

.hb-cell:hover {
  transform: scale(1.3);
  z-index: 1;
}

.hb-cell.success { background: #10b981; }
.hb-cell.skipped { background: var(--muted-foreground); opacity: 0.4; }
.hb-cell.error { background: #ef4444; }

@media (prefers-color-scheme: dark) {
  .hb-cell.skipped { background: #52525b; opacity: 1; }
}

/* --- Mobile --- */

@media (max-width: 639px) {
  main.page-content { padding: 0.75rem; }
  .card { padding: 1rem; }
  button { min-height: 2.75rem; }
  button.icon { width: 2.75rem; height: 2.75rem; }
  .chat-msg { max-width: 100%; }
  .sidebar nav a { min-height: 2.75rem; display: flex; align-items: center; }

  .chat-tab { padding: 0.25rem 0.375rem; font-size: 0.6875rem; }
  .chat-tab-title { max-width: 80px; }
  .chat-panel-messages .chat-msg { max-width: 100%; }
}
