376→/* --- Prose Bubble (Markdown in Chat) --- */
377→
378→.prose-bubble > *:first-child { margin-top: 0; }
379→.prose-bubble > *:last-child { margin-bottom: 0; }
380→
381→.prose-bubble p { margin: 0.4em 0; }
382→.prose-bubble h1, .prose-bubble h2, .prose-bubble h3,
383→.prose-bubble h4, .prose-bubble h5, .prose-bubble h6 {
384→  font-weight: 600;
385→  margin: 0.6em 0 0.3em;
386→}
387→.prose-bubble h1 { font-size: 1.25em; }
388→.prose-bubble h2 { font-size: 1.15em; }
389→.prose-bubble h3 { font-size: 1.05em; }
390→
391→.prose-bubble ul, .prose-bubble ol { margin: 0.4em 0; padding-left: 1.4em; }
392→.prose-bubble ul { list-style: disc; }
393→.prose-bubble ol { list-style: decimal; }
394→.prose-bubble li { margin: 0.15em 0; }
395→
396→.prose-bubble code {
397→  background: var(--muted);
398→  color: var(--accent-foreground);
399→  padding: 0.15em 0.35em;
400→  font-size: 0.875em;
401→  border-radius: 0.25rem;
402→}
403→.prose-bubble pre {
404→  background: var(--muted);
405→  padding: 0.75em;
406→  overflow-x: auto;
407→  margin: 0.5em 0;
408→  border-radius: 0.375rem;
409→}
410→.prose-bubble pre code {
411→  background: none;
412→  padding: 0;
413→  font-size: 0.85em;
414→}
415→
416→.prose-bubble blockquote {
417→  border-left: 3px solid var(--border);
418→  padding-left: 0.75em;
419→  margin: 0.4em 0;
420→  color: var(--muted-foreground);
421→}
422→
423→.prose-bubble table { border-collapse: collapse; margin: 0.5em 0; width: 100%; }
424→.prose-bubble th, .prose-bubble td {
425→  border: 1px solid var(--border);
426→  padding: 0.3em 0.6em;
427→  text-align: left;
428→}
429→.prose-bubble th { font-weight: 600; background: var(--muted); }
430→
431→.prose-bubble a { color: var(--primary); text-decoration: underline; }
432→.prose-bubble a:hover { opacity: 0.8; }
433→
434→.prose-bubble hr { border: none; border-top: 1px solid var(--border); margin: 0.6em 0; }
435→
436→/* User chat bubble: invert prose colors */
437→.chat-msg.user .prose-bubble a { color: var(--primary-foreground); }
438→.chat-msg.user .prose-bubble code { background: rgba(255,255,255,0.15); color: var(--primary-foreground); }
439→.chat-msg.user .prose-bubble pre { background: rgba(255,255,255,0.15); }
440→.chat-msg.user .prose-bubble blockquote { border-left-color: var(--primary-foreground); color: var(--primary-foreground); }
441→
442→/* --- Custom Scrollbar --- */
443→
444→.messages::-webkit-scrollbar { width: 6px; }
445→.messages::-webkit-scrollbar-track { background: transparent; }
446→.messages::-webkit-scrollbar-thumb { background: var(--border); border-radius: 3px; }
447→.messages::-webkit-scrollbar-thumb:hover { background: var(--muted-foreground); }
448→
449→/* --- Reduced Motion --- */
450→
451→@media (prefers-reduced-motion: reduce) {
452→  *, *::before, *::after {
453→    transition: none !important;
454→    animation: none !important;
455→  }
456→}
457→
458→/* --- Chat Footer --- */
459→
460→.chat-footer {
461→  display: flex;
462→  flex-direction: column;
463→  border-top: 1px solid var(--border);
464→  background: var(--card);
465→  min-height: 2.5rem;
466→  max-height: calc(100dvh - 80px);
467→  overflow: hidden;
468→  flex-shrink: 0;
469→}
470→
471→.chat-footer-handle {
472→  height: 6px;
473→  cursor: ns-resize;
474→  background: transparent;
475→  position: relative;
476→  flex-shrink: 0;
477→}
478→.chat-footer-handle::after {
479→  content: '';
480→  position: absolute;
481→  left: 50%;
482→  top: 50%;
483→  transform: translate(-50%, -50%);
484→  width: 32px;
485→  height: 3px;
486→  border-radius: 2px;
487→  background: var(--border);
488→  transition: background 0.15s;
489→}
490→.chat-footer-handle:hover::after {
491→  background: var(--muted-foreground);
492→}
493→
494→.chat-tab-bar {
495→  display: flex;
496→  align-items: center;
497→  gap: 0;
498→  border-bottom: 1px solid var(--border);
499→  padding: 0 0.25rem;
500→  flex-shrink: 0;
501→  overflow-x: auto;
502→  min-height: 2rem;
503→}
504→
505→.chat-tabs {
506→  display: flex;
507→  align-items: center;
508→  gap: 0;
509→  overflow-x: auto;
510→  flex: 1;
511→  min-width: 0;
512→}
513→
514→.chat-tab {
515→  display: inline-flex;
516→  align-items: center;
517→  gap: 0.375rem;
518→  padding: 0.25rem 0.5rem;
519→  font-size: 0.75rem;
520→  font-weight: 500;
521→  color: var(--muted-foreground);
522→  background: transparent;
523→  border: none;
524→  border-bottom: 2px solid transparent;
525→  border-radius: 0;
526→  height: 2rem;
527→  cursor: pointer;
528→  white-space: nowrap;
529→  transition: color 0.15s, border-color 0.15s;
530→}
531→.chat-tab:hover {
532→  color: var(--foreground);
533→  opacity: 1;
534→}
535→.chat-tab.active {
536→  color: var(--foreground);
537→  border-bottom-color: var(--primary);
538→}
539→
540→.chat-tab-title {
541→  max-width: 120px;
542→  overflow: hidden;
543→  text-overflow: ellipsis;
544→}
545→
546→.chat-tab-close {
547→  font-size: 0.875rem;
548→  line-height: 1;
549→  opacity: 0.5;
550→  cursor: pointer;
551→  padding: 0 0.125rem;
552→}
553→.chat-tab-close:hover {
554→  opacity: 1;
555→}
556→
557→.chat-tab-new {
558→  flex-shrink: 0;
559→  width: 2rem;
560→  height: 2rem;
561→  font-size: 1rem;
562→  padding: 0;
563→}
564→
565→.chat-panels {
566→  flex: 1;
567→  position: relative;
568→  overflow: hidden;
569→  min-height: 0;
570→}
571→
572→.chat-panel {
573→  display: none;
574→  position: absolute;
575→  inset: 0;
576→  flex-direction: column;
577→  overflow: hidden;
578→}
579→.chat-panel.active {
580→  display: flex;
581→}
582→
583→.chat-panel-inner {
584→  display: flex;
585→  flex-direction: column;
586→  height: 100%;
587→  overflow: hidden;
588→}
589→
590→.chat-panel-messages {
591→  flex: 1;
592→  overflow-y: auto;
593→  padding: 0.75rem;
594→  display: flex;
595→  flex-direction: column;
596→  gap: 0.5rem;
597→}
598→.chat-panel-messages::-webkit-scrollbar { width: 5px; }
599→.chat-panel-messages::-webkit-scrollbar-track { background: transparent; }
600→.chat-panel-messages::-webkit-scrollbar-thumb { background: var(--border); border-radius: 3px; }
601→
602→/* Compact message styles inside footer */
603→.chat-panel-messages .chat-msg {
604→  padding: 0.5rem 0.75rem;
605→  font-size: 0.8125rem;
606→  max-width: 90%;
607→}
608→.chat-panel-messages .msg-meta {
609→  font-size: 0.6rem;
610→}
611→.chat-panel-messages .prose-bubble p {
612→  margin: 0.25em 0;
613→}
614→
615→.chat-panel-form {
616→  display: flex;
617→  align-items: flex-end;
618→  gap: 0.5rem;
619→  padding: 0.5rem 0.75rem;
620→  border-top: 1px solid var(--border);
621→  flex-shrink: 0;
622→}
623→
624→.chat-panel-input {
625→  flex: 1;
626→  min-width: 0;
627→  resize: none;
628→  font-size: 0.8125rem;
629→  padding: 0.375rem 0.625rem;
630→  max-height: 120px;
631→}
632→
633→.chat-panel-loading {
634→  padding: 2rem;
635→  text-align: center;
636→  color: var(--muted-foreground);
637→  font-size: 0.8125rem;
638→}
639→
640→/* --- Conversation Canvas --- */
641→
642→.conversation-canvas {
643→  display: none;
644→  position: absolute;
645→  inset: 0;
646→  overflow: hidden;
647→  background: var(--background);
648→}
649→.conversation-canvas.active {
650→  display: block;
651→}
652→
653→/* Dot grid background */
654→.canvas-dot-grid {
655→  position: absolute;
656→  inset: 0;
657→  background-image: radial-gradient(circle, var(--border) 1px, transparent 1px);
658→  background-size: 24px 24px;
659→  pointer-events: none;
660→  z-index: 0;
661→}
662→
663→/* The pannable/zoomable world */
664→.canvas-world {
665→  position: absolute;
666→  top: 0;
667→  left: 0;
668→  transform-origin: 0 0;
669→  will-change: transform;
670→  z-index: 1;
671→}
672→
673→/* Individual canvas component */
674→.canvas-component {
675→  position: absolute;
676→  background: var(--card);
677→  border: 1px solid var(--border);
678→  border-radius: 0.5rem;
679→  box-shadow: 0 1px 3px 0 rgb(0 0 0 / 0.1), 0 1px 2px -1px rgb(0 0 0 / 0.1);
680→  cursor: grab;
681→  user-select: none;
682→  transition: box-shadow 0.15s, border-color 0.15s;
683→}
684→.canvas-component:hover {
685→  border-color: var(--primary);
686→  box-shadow: 0 4px 6px -1px rgb(0 0 0 / 0.1), 0 2px 4px -2px rgb(0 0 0 / 0.1);
687→}
688→.canvas-component.dragging {
689→  cursor: grabbing;
690→  box-shadow: 0 10px 15px -3px rgb(0 0 0 / 0.2), 0 4px 6px -4px rgb(0 0 0 / 0.2);
691→  border-color: var(--primary);
692→  z-index: 9999 !important;
693→  opacity: 0.95;
694→}
695→.canvas-component.canvas-component-highlight {
696→  border-color: var(--primary);
697→  box-shadow: 0 0 0 3px color-mix(in srgb, var(--primary) 30%, transparent), 0 4px 6px -1px rgb(0 0 0 / 0.1);
698→  animation: comp-highlight-pulse 0.6s ease-in-out 2;
699→}
700→@keyframes comp-highlight-pulse {
701→  0%, 100% { box-shadow: 0 0 0 3px color-mix(in srgb, var(--primary) 30%, transparent); }
702→  50% { box-shadow: 0 0 0 6px color-mix(in srgb, var(--primary) 15%, transparent); }
703→}
704→
705→.canvas-component-content {
706→  padding: 1rem;
707→  font-size: 0.875rem;
708→  overflow: hidden;
709→}
710→.canvas-component-content > *:first-child { margin-top: 0; }
711→.canvas-component-content > *:last-child { margin-bottom: 0; }
712→
713→/* Canvas context menu */
714→.canvas-context-menu {
715→  position: fixed;
716→  z-index: 10000;
717→  min-width: 180px;
718→  background: var(--card);
719→  border: 1px solid var(--border);
720→  border-radius: 0.5rem;
721→  box-shadow: 0 4px 6px -1px rgb(0 0 0 / 0.1), 0 2px 4px -2px rgb(0 0 0 / 0.1);
722→  padding: 0.25rem;
723→  animation: ctx-menu-in 0.12s ease-out;
724→}
725→
726→@keyframes ctx-menu-in {
727→  from { opacity: 0; transform: scale(0.95); }
728→  to { opacity: 1; transform: scale(1); }
729→}
730→
731→.canvas-context-menu-item {
732→  display: flex;
733→  align-items: center;
734→  gap: 0.5rem;
735→  width: 100%;
736→  height: auto;
737→  padding: 0.375rem 0.625rem;
738→  background: transparent;
739→  color: var(--foreground);
740→  font-size: 0.8125rem;
741→  font-weight: 400;
742→  border: none;
743→  border-radius: 0.25rem;
744→  cursor: pointer;
745→  text-align: left;
746→  justify-content: flex-start;
747→}
748→.canvas-context-menu-item:hover {
749→  background: var(--muted);
750→  opacity: 1;
751→}
752→
753→.canvas-context-menu-item.destructive {
754→  color: #ef4444;
755→}
756→.canvas-context-menu-item.destructive:hover {
757→  background: #fef2f2;
758→}
759→@media (prefers-color-scheme: dark) {
760→  .canvas-context-menu-item.destructive:hover {
761→    background: #451a1a;
762→  }
763→}
764→
765→.canvas-context-menu-separator {
766→  height: 1px;
767→  background: var(--border);
768→  margin: 0.25rem 0;
769→}
770→
771→/* Zoom indicator */
772→.canvas-zoom-indicator {
773→  position: absolute;
774→  bottom: 0.75rem;
775→  right: 0.75rem;
776→  background: var(--card);
777→  border: 1px solid var(--border);
778→  border-radius: 0.375rem;
779→  padding: 0.25rem 0.5rem;
780→  font-size: 0.7rem;
781→  color: var(--muted-foreground);
782→  z-index: 10;
783→  pointer-events: none;
784→}
785→
786→/* --- Mobile --- */
787→
788→@media (max-width: 639px) {
789→  main.page-content { padding: 0.75rem; }
790→  .card { padding: 1rem; }
791→  button { min-height: 2.75rem; }
792→  button.icon { width: 2.75rem; height: 2.75rem; }
793→  .chat-msg { max-width: 92%; }
794→  .sidebar nav a { min-height: 2.75rem; display: flex; align-items: center; }
795→
796→  .chat-tab { padding: 0.25rem 0.375rem; font-size: 0.6875rem; }
797→  .chat-tab-title { max-width: 80px; }
798→  .chat-panel-messages .chat-msg { max-width: 95%; }
799→}
800→

