content.content:
   1→/* ============================================
   2→   OpenFang — shadcn/ui Design System
   3→   Clean · Minimal · Terminal Green Accent · Light/Dark
   4→   ============================================ */
   5→
   6→/* --- Design Tokens (Light Mode) --- */
   7→:root {
   8→  --background: #d5d8dc;
   9→  --foreground: #1a1a1a;
  10→  --card: #c8ccd0;
  11→  --card-foreground: #1a1a1a;
  12→  --muted: #bcc1c6;
  13→  --muted-foreground: #4a5568;
  14→  --accent: #f0fdf4;
  15→  --accent-foreground: #14532d;
  16→  --primary: #15803d;
  17→  --primary-foreground: #ffffff;
  18→  --border: #b0b5bb;
  19→  --ring: #15803d;
  20→
  21→  /* Backward-compat aliases */
  22→  --fang-bg: var(--background);
  23→  --fang-fg: var(--foreground);
  24→  --fang-muted: var(--muted);
  25→  --fang-muted-fg: var(--muted-foreground);
  26→  --fang-accent: var(--primary);
  27→  --fang-accent-fg: var(--primary-foreground);
  28→  --fang-border: var(--border);
  29→  --fang-card: var(--card);
  30→}
  31→
  32→/* --- Dark Mode --- */
  33→@media (prefers-color-scheme: dark) {
  34→  :root {
  35→    --background: #09090b;
  36→    --foreground: #fafafa;
  37→    --card: #18181b;
  38→    --card-foreground: #fafafa;
  39→    --muted: #27272a;
  40→    --muted-foreground: #a1a1aa;
  41→    --accent: #27272a;
  42→    --accent-foreground: #22c55e;
  43→    --primary: #16a34a;
  44→    --primary-foreground: #ffffff;
  45→    --border: #27272a;
  46→    --ring: #16a34a;
  47→  }
  48→}
  49→
  50→/* --- Element Base Styles --- */
  51→
  52→body {
  53→  background: var(--background);
  54→  color: var(--foreground);
  55→  font-family: 'Inter', system-ui, sans-serif;
  56→  line-height: 1.5;
  57→}
  58→
  59→h1, h2, h3, h4, h5, h6 {
  60→  font-weight: 600;
  61→  line-height: 1.2;
  62→  letter-spacing: -0.025em;
  63→}
  64→
  65→h1 { font-size: 1.875rem; }
  66→h2 { font-size: 1.5rem; }
  67→h3 { font-size: 1.25rem; }
  68→h4 { font-size: 1.125rem; }
  69→h5 { font-size: 1rem; }
  70→h6 { font-size: 0.875rem; }
  71→
  72→a {
  73→  color: var(--primary);
  74→  text-decoration: none;
  75→  transition: color 0.2s;
  76→}
  77→a:hover {
  78→  text-decoration: underline;
  79→}
  80→
  81→button {
  82→  display: inline-flex;
  83→  align-items: center;
  84→  justify-content: center;
  85→  height: 2.5rem;
  86→  padding: 0 1rem;
  87→  background: var(--primary);
  88→  color: var(--primary-foreground);
  89→  font-family: inherit;
  90→  font-size: 0.875rem;
  91→  font-weight: 500;
  92→  border: none;
  93→  border-radius: 0.375rem;
  94→  cursor: pointer;
  95→  transition: opacity 0.2s;
  96→  white-space: nowrap;
  97→}
  98→button:hover {
  99→  opacity: 0.9;
 100→}
 101→button:active {
 102→  opacity: 0.8;
 103→}
 104→button:focus-visible {
 105→  outline: 2px solid var(--ring);
 106→  outline-offset: 2px;
 107→}
 108→
 109→input, textarea {
 110→  background: var(--background);
 111→  color: var(--foreground);
 112→  font-family: inherit;
 113→  font-size: 0.875rem;
 114→  border: 1px solid var(--border);
 115→  border-radius: 0.375rem;
 116→  padding: 0.5rem 0.75rem;
 117→  width: 100%;
 118→  outline: none;
 119→  transition: border-color 0.2s, box-shadow 0.2s;
 120→}
 121→input:focus, textarea:focus {
 122→  border-color: var(--ring);
 123→  box-shadow: 0 0 0 2px color-mix(in srgb, var(--ring) 25%, transparent);
 124→}
 125→input::placeholder, textarea::placeholder {
 126→  color: var(--muted-foreground);
 127→}
 128→
 129→table {
 130→  width: 100%;
 131→  border-collapse: collapse;
 132→  border: 1px solid var(--border);
 133→  border-radius: 0.5rem;
 134→  overflow: hidden;
 135→}
 136→th {
 137→  font-weight: 500;
 138→  font-size: 0.75rem;
 139→  color: var(--muted-foreground);
 140→  background: var(--muted);
 141→  text-align: left;
 142→  padding: 0.625rem 1rem;
 143→}
 144→td {
 145→  border-bottom: 1px solid var(--border);
 146→  padding: 0.625rem 1rem;
 147→  font-size: 0.875rem;
 148→}
 149→tr:last-child td {
 150→  border-bottom: none;
 151→}
 152→
 153→/* --- Data Table Widget --- */
 154→
 155→.data-table-widget {
 156→  display: flex;
 157→  flex-direction: column;
 158→  height: 100%;
 159→}
 160→
 161→.data-table-scroll {
 162→  overflow-y: auto;
 163→  overflow-x: auto;
 164→}
 165→.data-table-scroll::-webkit-scrollbar { width: 6px; height: 6px; }
 166→.data-table-scroll::-webkit-scrollbar-track { background: transparent; }
 167→.data-table-scroll::-webkit-scrollbar-thumb { background: var(--border); border-radius: 3px; }
 168→.data-table-scroll::-webkit-scrollbar-thumb:hover { background: var(--muted-foreground); }
 169→
 170→table.data-table {
 171→  table-layout: fixed;
 172→  min-width: max-content;
 173→}
 174→
 175→.data-table-th {
 176→  position: relative;
 177→  user-select: none;
 178→  white-space: nowrap;
 179→}
 180→
 181→th.data-table-th[data-sortable="true"] {
 182→  cursor: pointer;
 183→}
 184→th.data-table-th[data-sortable="true"]:hover {
 185→  color: var(--foreground);
 186→}
 187→
 188→th.sort-asc::after,
 189→th.sort-desc::after {
 190→  content: '';
 191→  display: inline-block;
 192→  width: 0;
 193→  height: 0;
 194→  margin-left: 0.375rem;
 195→  vertical-align: middle;
 196→  border-left: 4px solid transparent;
 197→  border-right: 4px solid transparent;
 198→}
 199→th.sort-asc::after {
 200→  border-bottom: 5px solid var(--primary);
 201→}
 202→th.sort-desc::after {
 203→  border-top: 5px solid var(--primary);
 204→}
 205→
 206→.col-resize-handle {
 207→  position: absolute;
 208→  right: 0;
 209→  top: 0;
 210→  bottom: 0;
 211→  width: 4px;
 212→  cursor: col-resize;
 213→  background: transparent;
 214→  transition: background 0.15s;
 215→}
 216→.col-resize-handle:hover {
 217→  background: var(--primary);
 218→}
 219→
 220→.data-table-truncate {
 221→  display: block;
 222→  max-width: 100%;
 223→  overflow: hidden;
 224→  text-overflow: ellipsis;
 225→  white-space: nowrap;
 226→}
 227→
 228→tr.data-table-next-page {
 229→  border: none;
 230→}
 231→tr.data-table-next-page td {
 232→  padding: 0;
 233→  border: none;
 234→  height: 1px;
 235→}
 236→
 237→hr {
 238→  border: none;
 239→  border-top: 1px solid var(--border);
 240→  margin: 1.5rem 0;
 241→}
 242→
 243→code {
 244→  background: var(--muted);
 245→  color: var(--accent-foreground);
 246→  padding: 0.15em 0.4em;
 247→  font-size: 0.875em;
 248→  border-radius: 0.25rem;
 249→}
 250→
 251→pre {
 252→  background: var(--muted);
 253→  padding: 1rem;
 254→  overflow-x: auto;
 255→  margin: 0.75rem 0;
 256→  border-radius: 0.5rem;
 257→}
 258→pre code {
 259→  background: none;
 260→  padding: 0;
 261→}
 262→
 263→/* --- Button Variants --- */
 264→
 265→button.ghost {
 266→  background: transparent;
 267→  color: var(--foreground);
 268→}
 269→button.ghost:hover {
 270→  background: var(--muted);
 271→  opacity: 1;
 272→}
 273→
 274→button.outline {
 275→  background: transparent;
 276→  border: 1px solid var(--border);
 277→  color: var(--foreground);
 278→}
 279→button.outline:hover {
 280→  background: var(--muted);
 281→  opacity: 1;
 282→}
 283→
 284→button.sm {
 285→  height: 2.25rem;
 286→  padding: 0 0.875rem;
 287→  font-size: 0.8125rem;
 288→}
 289→
 290→button.xs {
 291→  height: 1.75rem;
 292→  padding: 0 0.625rem;
 293→  font-size: 0.75rem;
 294→}
 295→
 296→button.icon {
 297→  width: 2.5rem;
 298→  height: 2.5rem;
 299→  padding: 0;
 300→}
 301→button.icon.sm {
 302→  width: 2rem;
 303→  height: 2rem;
 304→}
 305→
 306→/* --- Badge --- */
 307→
 308→.badge {
 309→  display: inline-flex;
 310→  align-items: center;
 311→  padding: 0.125rem 0.5rem;
 312→  font-size: 0.7rem;
 313→  font-weight: 500;
 314→  background: var(--muted);
 315→  color: var(--muted-foreground);
 316→  border-radius: 9999px;
 317→}
 318→.badge.success { background: #dcfce7; color: #166534; }
 319→.badge.error   { background: #fee2e2; color: #991b1b; }
 320→.badge.warning { background: #fef3c7; color: #92400e; }
 321→.badge.info    { background: #dbeafe; color: #1e40af; }
 322→
 323→@media (prefers-color-scheme: dark) {
 324→  .badge.success { background: #166534; color: #4ade80; }
 325→  .badge.error   { background: #7f1d1d; color: #f87171; }
 326→  .badge.warning { background: #78350f; color: #fbbf24; }
 327→  .badge.info    { background: #1e3a5f; color: #60a5fa; }
 328→}
 329→
 330→/* --- Card --- */
 331→
 332→.card {
 333→  border: 1px solid var(--border);
 334→  background: var(--card);
 335→  padding: 1.5rem;
 336→  border-radius: 0.5rem;
 337→  box-shadow: 0 1px 2px 0 rgb(0 0 0 / 0.05);
 338→  transition: border-color 0.2s;
 339→}
 340→
 341→/* --- Chat Messages (CLI-style) --- */
 342→
 343→.chat-msg {
 344→  padding: 0.75rem 1rem;
 345→  max-width: 100%;
 346→  border-radius: 0;
 347→  background: none;
 348→  border: none;
 349→  border-top: 1px solid var(--border);
 350→}
 351→.chat-msg:first-child { border-top: none; }
 352→
 353→.chat-msg.user {
 354→  margin-left: 0;
 355→  background: none;
 356→  color: var(--foreground);
 357→}
 358→.chat-msg.ai {
 359→  margin-right: 0;
 360→  background: none;
 361→  color: var(--foreground);
 362→}
 363→
 364→/* Role label styling */
 365→.chat-msg.user .msg-meta span:first-child {
 366→  color: var(--primary);
 367→  font-weight: 600;
 368→}
 369→.chat-msg.ai .msg-meta span:first-child {
 370→  color: var(--muted-foreground);
 371→  font-weight: 600;
 372→}
 373→
 374→/* --- Sidebar --- */
 375→
 376→#sidebar-toggle {
 377→  display: none;
 378→}
 379→
 380→.sidebar {
 381→  position: fixed;
 382→  top: 0;
 383→  left: 0;
 384→  width: 16rem;
 385→  height: 100dvh;
 386→  background: var(--card);
 387→  border-right: 1px solid var(--border);
 388→  transform: translateX(-100%);
 389→  transition: transform 0.2s ease;
 390→  z-index: 50;
 391→  overflow-y: auto;
 392→  padding: 1.5rem;
 393→  border-radius: 0 0.75rem 0.75rem 0;
 394→}
 395→
 396→#sidebar-toggle:checked ~ .sidebar-container .sidebar {
 397→  transform: translateX(0);
 398→}
 399→
 400→.sidebar-container::before {
 401→  content: '';
 402→  position: fixed;
 403→  inset: 0;
 404→  background: rgba(0, 0, 0, 0.4);
 405→  z-index: 40;
 406→  opacity: 0;
 407→  pointer-events: none;
 408→  transition: opacity 0.2s ease;
 409→}
 410→#sidebar-toggle:checked ~ .sidebar-container::before {
 411→  opacity: 1;
 412→  pointer-events: auto;
 413→}
 414→
 415→.sidebar nav a {
 416→  display: block;
 417→  padding: 0.5rem 0.75rem;
 418→  color: var(--muted-foreground);
 419→  font-size: 0.875rem;
 420→  font-weight: 500;
 421→  text-decoration: none;
 422→  border-radius: 0.375rem;
 423→  transition: background 0.15s, color 0.15s;
 424→}
 425→.sidebar nav a:hover {
 426→  background: var(--muted);
 427→  color: var(--foreground);
 428→  text-decoration: none;
 429→}
 430→
 431→/* --- Utility Classes --- */
 432→
 433→.section-heading {
 434→  font-size: 1.125rem;
 435→  font-weight: 600;
 436→  margin-bottom: 1rem;
 437→  letter-spacing: -0.025em;
 438→}
 439→
 440→.msg-meta {
 441→  font-size: 0.65rem;
 442→  text-transform: uppercase;
 443→  letter-spacing: 0.05em;
 444→  color: var(--muted-foreground);
 445→}
 446→
 447→.notification-unread {
 448→  border-left: 3px solid var(--primary);
 449→}
 450→
 451→/* --- Notifications Dropdown --- */
 452→
 453→.notifications-dropdown {
 454→  position: absolute;
 455→  top: calc(100% + 0.5rem);
 456→  right: 0;
 457→  z-index: 100;
 458→  width: 360px;
 459→  max-height: 480px;
 460→  background: var(--card);
 461→  border: 1px solid var(--border);
 462→  border-radius: 0.5rem;
 463→  box-shadow: 0 10px 15px -3px rgb(0 0 0 / 0.1), 0 4px 6px -4px rgb(0 0 0 / 0.1);
 464→  display: flex;
 465→  flex-direction: column;
 466→  animation: ctx-menu-in 0.12s ease-out;
 467→}
 468→
 469→.notifications-dropdown-header {
 470→  padding: 0.75rem 1rem;
 471→  border-bottom: 1px solid var(--border);
 472→  flex-shrink: 0;
 473→}
 474→
 475→.notifications-dropdown-list {
 476→  overflow-y: auto;
 477→  flex: 1;
 478→  min-height: 0;
 479→}
 480→
 481→.notifications-dropdown-list .card {
 482→  border-radius: 0;
 483→  border-left: none;
 484→  border-right: none;
 485→  border-top: none;
 486→  box-shadow: none;
 487→  padding: 0.75rem 1rem;
 488→}
 489→.notifications-dropdown-list .card:last-child {
 490→  border-bottom: none;
 491→}
 492→
 493→.notifications-dropdown-list .notification-unread {
 494→  border-left: 3px solid var(--primary);
 495→}
 496→
 497→.notifications-dropdown-footer {
 498→  border-top: 1px solid var(--border);
 499→  padding: 0.5rem;
 500→  flex-shrink: 0;
 501→}
 502→
 503→@media (max-width: 639px) {
 504→  .notifications-dropdown {
 505→    width: calc(100vw - 1rem);
 506→    right: -0.5rem;
 507→  }
 508→}
 509→
 510→/* --- Notification Card Submenu --- */
 511→
 512→.notification-submenu {
 513→  position: relative;
 514→}
 515→
 516→.notification-submenu > summary {
 517→  cursor: pointer;
 518→  list-style: none;
 519→  display: inline-flex;
 520→  align-items: center;
 521→  gap: 0.375rem;
 522→  height: 1.75rem;
 523→  padding: 0 0.625rem;
 524→  font-size: 0.75rem;
 525→  font-weight: 500;
 526→  background: transparent;
 527→  border: 1px solid var(--border);
 528→  color: var(--foreground);
 529→  border-radius: 0.375rem;
 530→  transition: background 0.15s;
 531→}
 532→.notification-submenu > summary:hover {
 533→  background: var(--muted);
 534→}
 535→.notification-submenu > summary::-webkit-details-marker {
 536→  display: none;
 537→}
 538→.notification-submenu > summary::after {
 539→  content: '';
 540→  display: inline-block;
 541→  width: 0;
 542→  height: 0;
 543→  border-left: 4px solid transparent;
 544→  border-right: 4px solid transparent;
 545→  border-top: 4px solid var(--muted-foreground);
 546→  margin-left: 0.25rem;
 547→}
 548→
 549→.notification-submenu-panel {
 550→  position: absolute;
 551→  bottom: calc(100% + 4px);
 552→  left: 0;
 553→  z-index: 50;
 554→  min-width: 220px;
 555→  max-width: 320px;
 556→  background: var(--card);
 557→  border: 1px solid var(--border);
 558→  border-radius: 0.5rem;
 559→  box-shadow: 0 4px 6px -1px rgb(0 0 0 / 0.1), 0 2px 4px -2px rgb(0 0 0 / 0.1);
 560→  padding: 0.25rem;
 561→  animation: ctx-menu-in 0.12s ease-out;
 562→}
 563→
 564→.notification-submenu-item {
 565→  display: flex;
 566→  flex-direction: column;
 567→  align-items: flex-start;
 568→  gap: 0.125rem;
 569→  width: 100%;
 570→  height: auto;
 571→  padding: 0.375rem 0.625rem;
 572→  background: transparent;
 573→  color: var(--foreground);
 574→  font-size: 0.8125rem;
 575→  font-weight: 400;
 576→  border: none;
 577→  border-radius: 0.25rem;
 578→  cursor: pointer;
 579→  text-align: left;
 580→}
 581→.notification-submenu-item:hover {
 582→  background: var(--muted);
 583→  opacity: 1;
 584→}
 585→
 586→.notification-submenu-new {
 587→  color: var(--primary);
 588→}
 589→
 590→.notification-submenu-section {
 591→  font-weight: 500;
 592→}
 593→
 594→.notification-submenu-divider {
 595→  height: 1px;
 596→  background: var(--border);
 597→  margin: 0.25rem 0;
 598→}
 599→
 600→.nav-trigger {
 601→  cursor: pointer;
 602→  display: inline-flex;
 603→  align-items: center;
 604→  justify-content: center;
 605→  width: 2.5rem;
 606→  height: 2.5rem;
 607→  border-radius: 0.375rem;
 608→  transition: background 0.15s;
 609→}
 610→.nav-trigger:hover {
 611→  background: var(--muted);
 612→}
 613→
 614→.notification-badge {
 615→  background: var(--primary);
 616→  color: var(--primary-foreground);
 617→  font-size: 0.6rem;
 618→  padding: 0.1rem 0.35rem;
 619→  border-radius: 9999px;
 620→  font-weight: 600;
 621→}
 622→
 623→/* --- Prose Bubble (Markdown in Chat) --- */
 624→
 625→.prose-bubble > *:first-child { margin-top: 0; }
 626→.prose-bubble > *:last-child { margin-bottom: 0; }
 627→
 628→.prose-bubble p { margin: 0.4em 0; }
 629→.prose-bubble h1, .prose-bubble h2, .prose-bubble h3,
 630→.prose-bubble h4, .prose-bubble h5, .prose-bubble h6 {
 631→  font-weight: 600;
 632→  margin: 0.6em 0 0.3em;
 633→}
 634→.prose-bubble h1 { font-size: 1.25em; }
 635→.prose-bubble h2 { font-size: 1.15em; }
 636→.prose-bubble h3 { font-size: 1.05em; }
 637→
 638→.prose-bubble ul, .prose-bubble ol { margin: 0.4em 0; padding-left: 1.4em; }
 639→.prose-bubble ul { list-style: disc; }
 640→.prose-bubble ol { list-style: decimal; }
 641→.prose-bubble li { margin: 0.15em 0; }
 642→
 643→.prose-bubble code {
 644→  background: var(--muted);
 645→  color: var(--accent-foreground);
 646→  padding: 0.15em 0.35em;
 647→  font-size: 0.875em;
 648→  border-radius: 0.25rem;
 649→}
 650→.prose-bubble pre {
 651→  background: var(--muted);
 652→  padding: 0.75em;
 653→  overflow-x: auto;
 654→  margin: 0.5em 0;
 655→  border-radius: 0.375rem;
 656→}
 657→.prose-bubble pre code {
 658→  background: none;
 659→  padding: 0;
 660→  font-size: 0.85em;
 661→}
 662→
 663→.prose-bubble blockquote {
 664→  border-left: 3px solid var(--border);
 665→  padding-left: 0.75em;
 666→  margin: 0.4em 0;
 667→  color: var(--muted-foreground);
 668→}
 669→
 670→.prose-bubble table { border-collapse: collapse; margin: 0.5em 0; width: 100%; }
 671→.prose-bubble th, .prose-bubble td {
 672→  border: 1px solid var(--border);
 673→  padding: 0.3em 0.6em;
 674→  text-align: left;
 675→}
 676→.prose-bubble th { font-weight: 600; background: var(--muted); }
 677→
 678→.prose-bubble a { color: var(--primary); text-decoration: underline; }
 679→.prose-bubble a:hover { opacity: 0.8; }
 680→
 681→.prose-bubble hr { border: none; border-top: 1px solid var(--border); margin: 0.6em 0; }
 682→
 683→/* --- Agent Steps (streaming progress) --- */
 684→
 685→.agent-steps {
 686→  display: flex;
 687→  flex-direction: column;
 688→  gap: 0.125rem;
 689→  margin-bottom: 0.5rem;
 690→}
 691→.agent-step {
 692→  display: flex;
 693→  align-items: center;
 694→  gap: 0.5rem;
 695→  font-size: 0.75rem;
 696→  color: var(--muted-foreground);
 697→}
 698→.agent-step-icon {
 699→  width: 0.5rem;
 700→  height: 0.5rem;
 701→  border-radius: 50%;
 702→  background: var(--muted-foreground);
 703→  flex-shrink: 0;
 704→}
 705→/* Pulse the active (last) step */
 706→.agent-step:last-child .agent-step-icon {
 707→  background: var(--primary);
 708→  animation: step-pulse 1.5s ease-in-out infinite;
 709→}
 710→@keyframes step-pulse {
 711→  0%, 100% { opacity: 1; }
 712→  50% { opacity: 0.3; }
 713→}
 714→
 715→/* --- Custom Scrollbar --- */
 716→
 717→.messages::-webkit-scrollbar { width: 6px; }
 718→.messages::-webkit-scrollbar-track { background: transparent; }
 719→.messages::-webkit-scrollbar-thumb { background: var(--border); border-radius: 3px; }
 720→.messages::-webkit-scrollbar-thumb:hover { background: var(--muted-foreground); }
 721→
 722→/* --- Reduced Motion --- */
 723→
 724→@media (prefers-reduced-motion: reduce) {
 725→  *, *::before, *::after {
 726→    transition: none !important;
 727→    animation: none !important;
 728→  }
 729→}
 730→
 731→/* --- Chat Footer --- */
 732→
 733→.chat-footer {
 734→  display: flex;
 735→  flex-direction: column;
 736→  border-top: 1px solid var(--border);
 737→  background: var(--card);
 738→  min-height: 2.5rem;
 739→  max-height: calc(100dvh - 80px);
 740→  overflow: hidden;
 741→  flex-shrink: 0;
 742→}
 743→
 744→.chat-footer-handle {
 745→  height: 6px;
 746→  cursor: ns-resize;
 747→  background: transparent;
 748→  position: relative;
 749→  flex-shrink: 0;
 750→}
 751→.chat-footer-handle::after {
 752→  content: '';
 753→  position: absolute;
 754→  left: 50%;
 755→  top: 50%;
 756→  transform: translate(-50%, -50%);
 757→  width: 32px;
 758→  height: 3px;
 759→  border-radius: 2px;
 760→  background: var(--border);
 761→  transition: background 0.15s;
 762→}
 763→.chat-footer-handle:hover::after {
 764→  background: var(--muted-foreground);
 765→}
 766→
 767→.chat-tab-bar {
 768→  display: flex;
 769→  align-items: center;
 770→  gap: 0;
 771→  border-bottom: 1px solid var(--border);
 772→  padding: 0 0.25rem;
 773→  flex-shrink: 0;
 774→  overflow-x: auto;
 775→  min-height: 2rem;
 776→}
 777→
 778→.chat-tabs {
 779→  display: flex;
 780→  align-items: center;
 781→  gap: 0;
 782→  overflow-x: auto;
 783→  flex: 1;
 784→  min-width: 0;
 785→}
 786→
 787→.chat-tab {
 788→  display: inline-flex;
 789→  align-items: center;
 790→  gap: 0.375rem;
 791→  padding: 0.25rem 0.5rem;
 792→  font-size: 0.75rem;
 793→  font-weight: 500;
 794→  color: var(--muted-foreground);
 795→  background: transparent;
 796→  border: none;
 797→  border-bottom: 2px solid transparent;
 798→  border-radius: 0;
 799→  height: 2rem;
 800→  cursor: pointer;
 801→  white-space: nowrap;
 802→  transition: color 0.15s, border-color 0.15s;
 803→}
 804→.chat-tab:hover {
 805→  color: var(--foreground);
 806→  opacity: 1;
 807→}
 808→.chat-tab.active {
 809→  color: var(--foreground);
 810→  border-bottom-color: var(--primary);
 811→}
 812→
 813→.chat-tab-title {
 814→  max-width: 120px;
 815→  overflow: hidden;
 816→  text-overflow: ellipsis;
 817→}
 818→
 819→.chat-tab-close {
 820→  font-size: 0.875rem;
 821→  line-height: 1;
 822→  opacity: 0.5;
 823→  cursor: pointer;
 824→  padding: 0 0.125rem;
 825→}
 826→.chat-tab-close:hover {
 827→  opacity: 1;
 828→}
 829→
 830→.chat-tab-new {
 831→  flex-shrink: 0;
 832→  width: 2rem;
 833→  height: 2rem;
 834→  font-size: 1rem;
 835→  padding: 0;
 836→}
 837→
 838→/* Two-level tab bars */
 839→.chat-tab-bar.canvas-level {
 840→  background: var(--muted);
 841→  border-bottom: none;
 842→}
 843→.chat-tab-bar.conversation-level {
 844→  min-height: 1.75rem;
 845→  padding-left: 0.5rem;
 846→}
 847→.chat-tab-bar.conversation-level .chat-tab {
 848→  font-size: 0.6875rem;
 849→  height: 1.75rem;
 850→}
 851→.chat-tab-bar.conversation-level .chat-tab-new {
 852→  width: 1.75rem;
 853→  height: 1.75rem;
 854→  font-size: 0.875rem;
 855→}
 856→
 857→.chat-panels {
 858→  flex: 1;
 859→  position: relative;
 860→  overflow: hidden;
 861→  min-height: 0;
 862→}
 863→
 864→.chat-panel {
 865→  display: none;
 866→  position: absolute;
 867→  inset: 0;
 868→  flex-direction: column;
 869→  overflow: hidden;
 870→}
 871→.chat-panel.active {
 872→  display: flex;
 873→}
 874→
 875→.chat-panel-inner {
 876→  display: flex;
 877→  flex-direction: column;
 878→  height: 100%;
 879→  overflow: hidden;
 880→}
 881→
 882→.chat-panel-messages {
 883→  flex: 1;
 884→  overflow-y: auto;
 885→  padding: 0.75rem;
 886→  display: flex;
 887→  flex-direction: column;
 888→  gap: 0.5rem;
 889→}
 890→.chat-panel-messages::-webkit-scrollbar { width: 5px; }
 891→.chat-panel-messages::-webkit-scrollbar-track { background: transparent; }
 892→.chat-panel-messages::-webkit-scrollbar-thumb { background: var(--border); border-radius: 3px; }
 893→
 894→/* Compact message styles inside footer */
 895→.chat-panel-messages .chat-msg {
 896→  padding: 0.5rem 0.75rem;
 897→  font-size: 0.8125rem;
 898→  max-width: 100%;
 899→}
 900→.chat-panel-messages .msg-meta {
 901→  font-size: 0.6rem;
 902→}
 903→.chat-panel-messages .prose-bubble p {
 904→  margin: 0.25em 0;
 905→}
 906→
 907→.chat-panel-form {
 908→  display: flex;
 909→  align-items: flex-end;
 910→  gap: 0.5rem;
 911→  padding: 0.5rem 0.75rem;
 912→  border-top: 1px solid var(--border);
 913→  flex-shrink: 0;
 914→}
 915→
 916→.chat-panel-input {
 917→  flex: 1;
 918→  min-width: 0;
 919→  resize: none;
 920→  font-size: 0.8125rem;
 921→  padding: 0.375rem 0.625rem;
 922→  max-height: 120px;
 923→}
 924→
 925→.chat-panel-loading {
 926→  padding: 2rem;
 927→  text-align: center;
 928→  color: var(--muted-foreground);
 929→  font-size: 0.8125rem;
 930→}
 931→
 932→/* --- Conversation Canvas --- */
 933→
 934→.conversation-canvas {
 935→  display: none;
 936→  position: absolute;
 937→  inset: 0;
 938→  overflow: hidden;
 939→  background: var(--background);
 940→}
 941→.conversation-canvas.active {
 942→  display: block;
 943→}
 944→
 945→/* Dot grid background */
 946→.canvas-dot-grid {
 947→  position: absolute;
 948→  inset: 0;
 949→  background-image: radial-gradient(circle, var(--border) 1px, transparent 1px);
 950→  background-size: 24px 24px;
 951→  pointer-events: none;
 952→  z-index: 0;
 953→}
 954→
 955→/* The pannable/zoomable world */
 956→.canvas-world {
 957→  position: absolute;
 958→  top: 0;
 959→  left: 0;
 960→  transform-origin: 0 0;
 961→  will-change: transform;
 962→  z-index: 1;
 963→}
 964→
 965→/* Individual canvas component */
 966→.canvas-component {
 967→  position: absolute;
 968→  background: var(--card);
 969→  border: 1px solid var(--border);
 970→  border-radius: 0.5rem;
 971→  box-shadow: 0 1px 3px 0 rgb(0 0 0 / 0.1), 0 1px 2px -1px rgb(0 0 0 / 0.1);
 972→  cursor: grab;
 973→  user-select: none;
 974→  transition: box-shadow 0.15s, border-color 0.15s;
 975→}
 976→.canvas-component:hover {
 977→  border-color: var(--primary);
 978→  box-shadow: 0 4px 6px -1px rgb(0 0 0 / 0.1), 0 2px 4px -2px rgb(0 0 0 / 0.1);
 979→}
 980→.canvas-component.dragging {
 981→  cursor: grabbing;
 982→  box-shadow: 0 10px 15px -3px rgb(0 0 0 / 0.2), 0 4px 6px -4px rgb(0 0 0 / 0.2);
 983→  border-color: var(--primary);
 984→  z-index: 9999 !important;
 985→  opacity: 0.95;
 986→}
 987→.canvas-component.canvas-component-highlight {
 988→  border-color: var(--primary);
 989→  box-shadow: 0 0 0 3px color-mix(in srgb, var(--primary) 30%, transparent), 0 4px 6px -1px rgb(0 0 0 / 0.1);
 990→  animation: comp-highlight-pulse 0.6s ease-in-out 2;
 991→}
 992→@keyframes comp-highlight-pulse {
 993→  0%, 100% { box-shadow: 0 0 0 3px color-mix(in srgb, var(--primary) 30%, transparent); }
 994→  50% { box-shadow: 0 0 0 6px color-mix(in srgb, var(--primary) 15%, transparent); }
 995→}
 996→
 997→.canvas-component-content {
 998→  padding: 1rem;
 999→  font-size: 0.875rem;
1000→  overflow: hidden;
1001→}
1002→.canvas-component-content[contenteditable="true"] {
1003→  outline: 2px solid var(--ring);
1004→  outline-offset: -2px;
1005→  border-radius: 0.375rem;
1006→  cursor: text;
1007→  overflow: auto;
1008→}
1009→.canvas-component-content > *:first-child { margin-top: 0; }
1010→.canvas-component-content > *:last-child { margin-bottom: 0; }
1011→
1012→/* Canvas context menu */
1013→.canvas-context-menu {
1014→  position: fixed;
1015→  z-index: 10000;
1016→  min-width: 180px;
1017→  background: var(--card);
1018→  border: 1px solid var(--border);
1019→  border-radius: 0.5rem;
1020→  box-shadow: 0 4px 6px -1px rgb(0 0 0 / 0.1), 0 2px 4px -2px rgb(0 0 0 / 0.1);
1021→  padding: 0.25rem;
1022→  animation: ctx-menu-in 0.12s ease-out;
1023→}
1024→
1025→@keyframes ctx-menu-in {
1026→  from { opacity: 0; transform: scale(0.95); }
1027→  to { opacity: 1; transform: scale(1); }
1028→}
1029→
1030→.canvas-context-menu-item {
1031→  display: flex;
1032→  align-items: center;
1033→  gap: 0.5rem;
1034→  width: 100%;
1035→  height: auto;
1036→  padding: 0.375rem 0.625rem;
1037→  background: transparent;
1038→  color: var(--foreground);
1039→  font-size: 0.8125rem;
1040→  font-weight: 400;
1041→  border: none;
1042→  border-radius: 0.25rem;
1043→  cursor: pointer;
1044→  text-align: left;
1045→  justify-content: flex-start;
1046→}
1047→.canvas-context-menu-item:hover {
1048→  background: var(--muted);
1049→  opacity: 1;
1050→}
1051→
1052→.canvas-context-menu-item.destructive {
1053→  color: #ef4444;
1054→}
1055→.canvas-context-menu-item.destructive:hover {
1056→  background: #fef2f2;
1057→}
1058→@media (prefers-color-scheme: dark) {
1059→  .canvas-context-menu-item.destructive:hover {
1060→    background: #451a1a;
1061→  }
1062→}
1063→
1064→.canvas-context-menu-separator {
1065→  height: 1px;
1066→  background: var(--border);
1067→  margin: 0.25rem 0;
1068→}
1069→
1070→/* Zoom indicator */
1071→.canvas-zoom-indicator {
1072→  position: absolute;
1073→  bottom: 0.75rem;
1074→  right: 0.75rem;
1075→  background: var(--card);
1076→  border: 1px solid var(--border);
1077→  border-radius: 0.375rem;
1078→  padding: 0.25rem 0.5rem;
1079→  font-size: 0.7rem;
1080→  color: var(--muted-foreground);
1081→  z-index: 10;
1082→  pointer-events: none;
1083→  display: flex;
1084→  align-items: center;
1085→  gap: 0.25rem;
1086→}
1087→.canvas-scroll-lock-btn {
1088→  all: unset;
1089→  cursor: pointer;
1090→  padding: 0.125rem;
1091→  border-radius: 0.25rem;
1092→  line-height: 1;
1093→  font-size: 0.8rem;
1094→  pointer-events: auto;
1095→}
1096→.canvas-scroll-lock-btn:hover {
1097→  background: var(--muted);
1098→}
1099→
1100→/* --- Scroll Lock Mode --- */
1101→.conversation-canvas.scroll-locked .canvas-dot-grid {
1102→  display: none;
1103→}
1104→
1105→/* --- Heartbeat Grid --- */
1106→
1107→@keyframes heartbeat-pulse {
1108→  0%, 100% { opacity: 1; transform: scale(1); }
1109→  50% { opacity: 0.7; transform: scale(1.15); }
1110→}
1111→
1112→.hb-grid table { width: 100%; }
1113→.hb-grid th, .hb-grid td { vertical-align: middle; }
1114→
1115→.hb-cell {
1116→  display: inline-block;
1117→  width: 16px;
1118→  height: 16px;
1119→  border-radius: 3px;
1120→  background: var(--muted);
1121→  flex-shrink: 0;
1122→  cursor: default;
1123→  transition: transform 0.1s ease;
1124→}
1125→
1126→.hb-cell:hover {
1127→  transform: scale(1.3);
1128→  z-index: 1;
1129→}
1130→
1131→.hb-cell.success { background: #10b981; }
1132→.hb-cell.skipped { background: var(--muted-foreground); opacity: 0.4; }
1133→.hb-cell.error { background: #ef4444; }
1134→
1135→@media (prefers-color-scheme: dark) {
1136→  .hb-cell.skipped { background: #52525b; opacity: 1; }
1137→}
1138→
1139→/* --- Mobile --- */
1140→
1141→@media (max-width: 639px) {
1142→  main.page-content { padding: 0.75rem; }
1143→  .card { padding: 1rem; }
1144→  button { min-height: 2.75rem; }
1145→  button.icon { width: 2.75rem; height: 2.75rem; }
1146→  .chat-msg { max-width: 100%; }
1147→  .sidebar nav a { min-height: 2.75rem; display: flex; align-items: center; }
1148→
1149→  .chat-tab { padding: 0.25rem 0.375rem; font-size: 0.6875rem; }
1150→  .chat-tab-title { max-width: 80px; }
1151→  .chat-panel-messages .chat-msg { max-width: 100%; }
1152→}
1153→

