/* ============================================
   OpenFang — shadcn/ui Design System
   Clean · Minimal · Terminal Green Accent · Light/Dark
   ============================================ */

/* --- Design Tokens (Light Mode) --- */
:root {
  --background: #c8ccd1;
  --foreground: #0f172a;
  --card: #dde0e4;
  --card-foreground: #0f172a;
  --muted: #b8bcc2;
  --muted-foreground: #4a5568;
  --accent: #e8f5ec;
  --accent-foreground: #14532d;
  --primary: #16a34a;
  --primary-foreground: #ffffff;
  --border: #a8adb4;
  --ring: #16a34a;
  --input: #e8eaee;
  --destructive: #dc2626;
  --destructive-foreground: #ffffff;

  /* Shadow scale */
  --shadow-sm: 0 1px 2px 0 rgb(0 0 0 / 0.05);
  --shadow-md: 0 4px 6px -1px rgb(0 0 0 / 0.1), 0 2px 4px -2px rgb(0 0 0 / 0.1);
  --shadow-lg: 0 10px 15px -3px rgb(0 0 0 / 0.1), 0 4px 6px -4px rgb(0 0 0 / 0.1);

  /* Border radius scale */
  --radius-sm: 0.25rem;
  --radius-md: 0.375rem;
  --radius-lg: 0.5rem;

  /* Backward-compat aliases */
  --fang-bg: var(--background);
  --fang-fg: var(--foreground);
  --fang-muted: var(--muted);
  --fang-muted-fg: var(--muted-foreground);
  --fang-accent: var(--primary);
  --fang-accent-fg: var(--primary-foreground);
  --fang-border: var(--border);
  --fang-card: var(--card);
}

/* --- Dark Mode --- */
@media (prefers-color-scheme: dark) {
  :root {
    --background: #09090b;
    --foreground: #fafafa;
    --card: #18181b;

  }
  this.activeEventSource = source
}

// --- Message Sending ---

async sendMessage(event) {
  event.preventDefault()
  const form = event.target
  const textarea = form.querySelector("textarea")
  const content = textarea.value.trim()
  if (!content) return

  const conversationId = form.querySelector("[name=conversation_id]").value

  // Append user message immediately
  const messagesEl = document.getElementById(`messages-${conversationId}`)
  if (messagesEl) {
    const emptyState = messagesEl.querySelector(".py-8.text-center")
    if (emptyState) emptyState.remove()

    const msgDiv = document.createElement("div")
    msgDiv.className = "chat-msg user"
    msgDiv.innerHTML = `
      <div class="msg-meta flex items-center gap-2 mb-1">
        <span>YOU</span>
        <time>${new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}</time>
      </div>
      <div class="prose-bubble"><p>${this.escapeHtml(content)}</p></div>
    `
    messagesEl.appendChild(msgDiv)
    requestAnimationFrame(() => { messagesEl.scrollTop = messagesEl.scrollHeight })
  }

  textarea.value = ""
  textarea.style.height = "auto"

  // Show AI thinking indicator
  if (messagesEl) {
    const thinkingDiv = document.createElement("div")
    thinkingDiv.className = "ai-thinking"
    thinkingDiv.id = `thinking-${conversationId}`
    thinkingDiv.innerHTML = `
      <div class="ai-thinking-dots">
        <div class="ai-thinking-dot"></div>
        <div class="ai-thinking-dot"></div>
        <div class="ai-thinking-dot"></div>
      </div>
    `
    messagesEl.appendChild(thinkingDiv)
    requestAnimationFrame(() => { messagesEl.scrollTop = messagesEl.scrollHeight })
  }

  try {
    await fetch(`/conversations/${conversationId}/messages`, {
      method: "POST",
      headers: { "Content-Type": "application/x-www-form-urlencoded" },
      body: `content=${encodeURIComponent(content)}`
    })
  } catch (e) {
    console.error("[ChatFooter] Failed to send message:", e)
  }
}

handleKeydown(event) {
  if (event.key === "Enter" && !event.shiftKey) {
    event.preventDefault()
    event.target.closest("form").requestSubmit()
  }
}

