 660→}
 661→.prose-bubble h1 { font-size: 1.25em; }
 662→.prose-bubble h2 { font-size: 1.15em; }
 663→.prose-bubble h3 { font-size: 1.1em; }
 664→
 665→.prose-bubble ul, .prose-bubble ol { margin: 0.4em 0; padding-left: 1.4em; }
 666→.prose-bubble ul { list-style: disc; }
 667→.prose-bubble ol { list-style: decimal; }
 668→.prose-bubble li { margin: 0.15em 0; }
 669→
 670→.prose-bubble code {
 671→  background: var(--muted);
 672→  color: var(--accent-foreground);
 673→  padding: 0.15em 0.35em;
 674→  font-size: 0.875em;
 675→  border-radius: 0.25rem;
 676→}
 677→.prose-bubble pre {
 678→  background: var(--muted);
 679→  padding: 0.75em;
 680→  overflow-x: auto;
 681→  margin: 0.5em 0;
 682→  border-radius: 0.375rem;
 683→}
 684→.prose-bubble pre code {
 685→  background: none;
 686→  padding: 0;
 687→  font-size: 0.85em;
 688→}
 689→
 690→.prose-bubble blockquote {
 691→  border-left: 3px solid var(--border);
 692→  padding-left: 0.75em;
 693→  margin: 0.4em 0;
 694→  color: var(--muted-foreground);
 695→}
 696→
 697→.prose-bubble table { border-collapse: collapse; margin: 0.5em 0; width: 100%; }
 698→.prose-bubble th, .prose-bubble td {
 699→  border: 1px solid var(--border);
 700→  padding: 0.3em 0.6em;
 701→  text-align: left;
 702→}
 703→.prose-bubble th { font-weight: 600; background: var(--muted); }
 704→
 705→.prose-bubble a { color: var(--primary); text-decoration: underline; }
 706→.prose-bubble a:hover { opacity: 0.8; }
 707→
 708→.prose-bubble hr { border: none; border-top: 1px solid var(--border); margin: 0.6em 0; }
 709→
 710→/* --- Agent Steps (streaming progress) --- */
 711→
 712→.agent-steps {
 713→  display: flex;
 714→  flex-direction: column;
 715→  gap: 0.125rem;
 716→  margin-bottom: 0.5rem;
 717→}
 718→.agent-step {
 719→  display: flex;
 720→  align-items: center;
 721→  gap: 0.5rem;
 722→  font-size: 0.75rem;
 723→  color: var(--muted-foreground);
 724→}
 725→.agent-step-icon {
 726→  width: 0.5rem;
 727→  height: 0.5rem;
 728→  border-radius: 50%;
 729→  background: var(--muted-foreground);
 730→  flex-shrink: 0;
 731→}
 732→/* Pulse the active (last) step */
 733→.agent-step:last-child .agent-step-icon {
 734→  background: var(--primary);
 735→  animation: step-pulse 1.5s ease-in-out infinite;
 736→}
 737→@keyframes step-pulse {
 738→  0%, 100% { opacity: 1; }
 739→  50% { opacity: 0.3; }
 740→}
 741→
 742→/* --- Action Button Feedback --- */
 743→
 744→.fang-spinner {
 745→  display: inline-block;
 746→  width: 0.875rem;
 747→  height: 0.875rem;
 748→  border: 2px solid currentColor;
 749→  border-right-color: transparent;
 750→  border-radius: 50%;
 751→  animation: fang-spin 0.6s linear infinite;
 752→}
 753→
 754→@keyframes fang-spin {
 755→  to { transform: rotate(360deg); }
 756→}
 757→
 758→.fang-action-loading {
 759→  opacity: 0.7;
 760→  pointer-events: none;
 761→}
 762→
 763→.fang-action-success {
 764→  background: #16a34a !important;
 765→  color: #fff !important;
 766→}
 767→
 768→.fang-action-error {
 769→  background: var(--destructive) !important;
 770→  color: var(--destructive-foreground) !important;
 771→}
 772→
 773→/* --- AI Thinking Indicator --- */
 774→
 775→.ai-thinking {
 776→  display: flex;
 777→  align-items: center;
 778→  gap: 0.375rem;
 779→  padding: 0.5rem 0.75rem;
 780→}
 781→.ai-thinking-dots {
 782→  display: flex;
 783→  gap: 0.25rem;
 784→}
 785→.ai-thinking-dot {
 786→  width: 0.375rem;
 787→  height: 0.375rem;
 788→  border-radius: 50%;
 789→  background: var(--muted-foreground);
 790→  animation: ai-think-pulse 1.4s ease-in-out infinite;
 791→}
 792→.ai-thinking-dot:nth-child(2) { animation-delay: 0.2s; }
 793→.ai-thinking-dot:nth-child(3) { animation-delay: 0.4s; }
 794→
 795→@keyframes ai-think-pulse {
 796→  0%, 80%, 100% { opacity: 0.3; transform: scale(0.8); }
 797→  40% { opacity: 1; transform: scale(1); }
 798→}
 799→
 800→/* --- Reduced Motion --- */
 801→
 802→@media (prefers-reduced-motion: reduce) {
 803→  *, *::before, *::after {
 804→    transition: none !important;
 805→    animation: none !important;
 806→  }
 807→}
 808→
 809→/* --- Chat Footer --- */
 810→
 811→.chat-footer {
 812→  display: flex;
 813→  flex-direction: column;
 814→  border-top: 1px solid var(--border);
 815→  background: var(--card);
 816→  min-height: 2.5rem;
 817→  max-height: calc(100dvh - 80px);
 818→  overflow: hidden;
 819→  flex-shrink: 0;
 820→  transition: height 0.15s ease;
 821→}
 822→
 823→.chat-footer-handle {
 824→  height: 6px;
 825→  cursor: ns-resize;
 826→  background: transparent;
 827→  position: relative;
 828→  flex-shrink: 0;
 829→}
 830→.chat-footer-handle::after {
 831→  content: '';
 832→  position: absolute;
 833→  left: 50%;
 834→  top: 50%;
 835→  transform: translate(-50%, -50%);
 836→  width: 32px;
 837→  height: 3px;
 838→  border-radius: 2px;
 839→  background: var(--border);
 840→  transition: background 0.15s;
 841→}
 842→.chat-footer-handle:hover::after {
 843→  background: var(--muted-foreground);
 844→}
 845→
 846→.chat-tab-bar {
 847→  display: flex;
 848→  align-items: center;
 849→  gap: 0;
 850→  border-bottom: 1px solid var(--border);
 851→  padding: 0 0.25rem;
 852→  flex-shrink: 0;
 853→  overflow-x: auto;
 854→  min-height: 2rem;
 855→}
 856→
 857→.chat-tabs {
 858→  display: flex;
 859→  align-items: center;
 860→  gap: 0;
 861→  overflow-x: auto;
 862→  flex: 1;
 863→  min-width: 0;
 864→}
 865→
 866→.chat-tab {
 867→  display: inline-flex;
 868→  align-items: center;
 869→  gap: 0.375rem;
 870→  padding: 0.25rem 0.5rem;
 871→  font-size: 0.75rem;
 872→  font-weight: 500;
 873→  color: var(--muted-foreground);
 874→  background: transparent;
 875→  border: none;
 876→  border-bottom: 2px solid transparent;
 877→  border-radius: 0;
 878→  height: 2rem;
 879→  cursor: pointer;
 880→  white-space: nowrap;
 881→  transition: color 0.15s, border-color 0.15s;
 882→}
 883→.chat-tab:hover {
 884→  color: var(--foreground);
 885→}
 886→.chat-tab:focus-visible {
 887→  outline: 2px solid var(--ring);
 888→  outline-offset: -2px;
 889→}
 890→.chat-tab.active {
 891→  color: var(--foreground);
 892→  border-bottom-color: var(--primary);
 893→}
 894→
 895→.chat-tab-title {
 896→  max-width: 120px;
 897→  overflow: hidden;
 898→  text-overflow: ellipsis;
 899→}
 900→
 901→.chat-tab-close {
 902→  font-size: 0.875rem;
 903→  line-height: 1;
 904→  opacity: 0.5;
 905→  cursor: pointer;
 906→  padding: 0 0.125rem;
 907→  border-radius: var(--radius-sm);
 908→}
 909→.chat-tab-close:hover {
 910→  opacity: 1;
 911→}
 912→.chat-tab-close:focus-visible {
 913→  outline: 2px solid var(--ring);
 914→  outline-offset: 1px;
 915→  opacity: 1;
 916→}
 917→
 918→.chat-tab-new {
 919→  flex-shrink: 0;
 920→  width: 2rem;
 921→  height: 2rem;
 922→  font-size: 1rem;
 923→  padding: 0;
 924→}
 925→
 926→/* Two-level tab bars */
 927→.chat-tab-bar.canvas-level {
 928→  background: var(--muted);
 929→  border-bottom: none;
 930→}
 931→.chat-tab-bar.conversation-level {
 932→  min-height: 1.75rem;
 933→  padding-left: 0.5rem;
 934→}
 935→.chat-tab-bar.conversation-level .chat-tab {
 936→  font-size: 0.6875rem;
 937→  height: 1.75rem;
 938→}
 939→.chat-tab-bar.conversation-level .chat-tab-new {
 940→  width: 1.75rem;
 941→  height: 1.75rem;
 942→  font-size: 0.875rem;
 943→}
 944→
 945→.chat-panels {
 946→  flex: 1;
 947→  position: relative;
 948→  overflow: hidden;
 949→  min-height: 0;
 950→}
 951→
 952→.chat-panel {
 953→  display: none;
 954→  position: absolute;
 955→  inset: 0;
 956→  flex-direction: column;
 957→  overflow: hidden;
 958→}
 959→.chat-panel.active {
 960→  display: flex;
 961→}
 962→
 963→.chat-panel-inner {
 964→  display: flex;
 965→  flex-direction: column;
 966→  height: 100%;
 967→  overflow: hidden;
 968→}
 969→
 970→.chat-panel-messages {
 971→  flex: 1;
 972→  overflow-y: auto;
 973→  padding: 0.75rem;
 974→  display: flex;
 975→  flex-direction: column;
 976→  gap: 0.5rem;
 977→}
 978→.chat-panel-messages::-webkit-scrollbar { width: 5px; }
 979→.chat-panel-messages::-webkit-scrollbar-track { background: transparent; }
 980→.chat-panel-messages::-webkit-scrollbar-thumb { background: var(--border); border-radius: 3px; }
 981→
 982→/* Compact message styles inside footer */
 983→.chat-panel-messages .chat-msg {
 984→  padding: 0.5rem 0.75rem;
 985→  font-size: 0.8125rem;
 986→  max-width: 100%;
 987→}
 988→.chat-panel-messages .msg-meta {
 989→  font-size: 0.625rem;
 990→}
 991→.chat-panel-messages .prose-bubble p {
 992→  margin: 0.25em 0;
 993→}
 994→
 995→.chat-panel-form {
 996→  display: flex;
 997→  align-items: flex-end;
 998→  gap: 0.5rem;
 999→  padding: 0.5rem 0.75rem;
1000→  border-top: 1px solid var(--border);
1001→  flex-shrink: 0;
1002→}
1003→
1004→.chat-panel-input {
1005→  flex: 1;
1006→  min-width: 0;
1007→  resize: none;
1008→  font-size: 0.8125rem;
1009→  padding: 0.375rem 0.625rem;
1010→  max-height: 120px;
1011→}
1012→
1013→.chat-panel-loading {
1014→  padding: 2rem;
1015→  text-align: center;
1016→  color: var(--muted-foreground);
1017→  font-size: 0.8125rem;
1018→}
1019→
1020→/* --- Conversation Canvas --- */
1021→
1022→.conversation-canvas {
1023→  display: none;
1024→  position: absolute;
1025→  inset: 0;
1026→  overflow: hidden;
1027→  background: var(--background);
1028→}
1029→.conversation-canvas.active {
1030→  display: block;
1031→}
1032→
1033→/* Dot grid background */
1034→.canvas-dot-grid {
1035→  position: absolute;
1036→  inset: 0;
1037→  background-image: radial-gradient(circle, var(--border) 1px, transparent 1px);
1038→  background-size: 24px 24px;
1039→  pointer-events: none;
1040→  z-index: 0;
1041→}
1042→
1043→/* The pannable/zoomable world */
1044→.canvas-world {
1045→  position: absolute;
1046→  top: 0;
1047→  left: 0;
1048→  transform-origin: 0 0;
1049→  will-change: transform;
1050→  z-index: 1;
1051→}
1052→
1053→/* Individual canvas component */
1054→.canvas-component {
1055→  position: absolute;
1056→  background: var(--card);
1057→  border: 1px solid var(--border);
1058→  border-radius: var(--radius-lg);
1059→  box-shadow: var(--shadow-sm);
1060→  cursor: grab;
1061→  user-select: none;
1062→  transition: box-shadow 0.15s, border-color 0.15s;
1063→}
1064→.canvas-component:hover {
1065→  border-color: var(--primary);
1066→  box-shadow: var(--shadow-md);
1067→}
1068→.canvas-component.dragging {
1069→  cursor: grabbing;
1070→  box-shadow: var(--shadow-lg);
1071→  border-color: var(--primary);
1072→  z-index: 9999 !important;
1073→  opacity: 0.95;
1074→}
1075→.canvas-component.canvas-component-highlight {
1076→  border-color: var(--primary);
1077→  box-shadow: 0 0 0 3px color-mix(in srgb, var(--primary) 30%, transparent), 0 4px 6px -1px rgb(0 0 0 / 0.1);
1078→  animation: comp-highlight-pulse 0.6s ease-in-out 2;
1079→}
1080→@keyframes comp-highlight-pulse {
1081→  0%, 100% { box-shadow: 0 0 0 3px color-mix(in srgb, var(--primary) 30%, transparent); }
1082→  50% { box-shadow: 0 0 0 6px color-mix(in srgb, var(--primary) 15%, transparent); }
1083→}
1084→
1085→.canvas-component-content {
1086→  padding: 1rem;
1087→  font-size: 0.875rem;
1088→  overflow: hidden;
1089→}
1090→.canvas-component-content[contenteditable="true"] {
1091→  outline: 2px solid var(--ring);
1092→  outline-offset: -2px;
1093→  border-radius: 0.375rem;
1094→  cursor: text;
1095→  overflow: auto;
1096→}
1097→.canvas-component-content > *:first-child { margin-top: 0; }
1098→.canvas-component-content > *:last-child { margin-bottom: 0; }
1099→
1100→/* Canvas context menu */
1101→.canvas-context-menu {
1102→  position: fixed;
1103→  z-index: 10000;
1104→  min-width: 180px;
1105→  background: var(--card);
1106→  border: 1px solid var(--border);
1107→  border-radius: var(--radius-lg);
1108→  box-shadow: var(--shadow-md);
1109→  padding: 0.25rem;
1110→  animation: ctx-menu-in 0.12s ease-out;
1111→  transform-origin: top left;
1112→}
1113→
1114→@keyframes ctx-menu-in {
1115→  from { opacity: 0; transform: scale(0.95); }
1116→  to { opacity: 1; transform: scale(1); }
1117→}
1118→
1119→.canvas-context-menu-item {
1120→  display: flex;
1121→  align-items: center;
1122→  gap: 0.5rem;
1123→  width: 100%;
1124→  height: auto;
1125→  padding: 0.375rem 0.625rem;
1126→  background: transparent;
1127→  color: var(--foreground);
1128→  font-size: 0.8125rem;
1129→  font-weight: 400;
1130→  border: none;
1131→  border-radius: 0.25rem;
1132→  cursor: pointer;
1133→  text-align: left;
1134→  justify-content: flex-start;
1135→}
1136→.canvas-context-menu-item:hover {
1137→  background: var(--muted);
1138→}
1139→.canvas-context-menu-item:focus-visible {
1140→  outline: 2px solid var(--ring);
1141→  outline-offset: -2px;
1142→}
1143→
1144→.canvas-context-menu-item.destructive {
1145→  color: var(--destructive);
1146→}
1147→.canvas-context-menu-item.destructive:hover {
1148→  background: color-mix(in srgb, var(--destructive) 10%, transparent);
1149→}
1150→
1151→.canvas-context-menu-separator {
1152→  height: 1px;
1153→  background: var(--border);
1154→  margin: 0.25rem 0;
1155→}
1156→
1157→/* Zoom indicator */
1158→.canvas-zoom-indicator {
1159→  position: absolute;
1160→  bottom: 0.75rem;
1161→  right: 0.75rem;
1162→  background: var(--card);
1163→  border: 1px solid var(--border);
1164→  border-radius: var(--radius-md);
1165→  padding: 0.25rem 0.5rem;
1166→  font-size: 0.7rem;
1167→  color: var(--muted-foreground);
1168→  z-index: 10;
1169→  pointer-events: none;
1170→  display: flex;
1171→  align-items: center;
1172→  gap: 0.25rem;
1173→}
1174→.canvas-scroll-lock-btn {
1175→  display: inline-flex;
1176→  align-items: center;
1177→  justify-content: center;
1178→  background: transparent;
1179→  border: none;
1180→  cursor: pointer;
1181→  padding: 0.125rem;
1182→  border-radius: var(--radius-sm);
1183→  line-height: 1;
1184→  font-size: 0.8rem;
1185→  pointer-events: auto;
1186→  color: inherit;
1187→}
1188→.canvas-scroll-lock-btn:hover {
1189→  background: var(--muted);
1190→}
1191→.canvas-scroll-lock-btn:focus-visible {
1192→  outline: 2px solid var(--ring);
1193→  outline-offset: 2px;
1194→}
1195→
1196→/* --- Scroll Lock Mode --- */
1197→.conversation-canvas.scroll-locked .canvas-dot-grid {
1198→  display: none;
1199→}
1200→
1201→/* --- Heartbeat Grid --- */
1202→
1203→@keyframes heartbeat-pulse {
1204→  0%, 100% { opacity: 1; transform: scale(1); }
1205→  50% { opacity: 0.7; transform: scale(1.15); }
1206→}
1207→
1208→.hb-grid table { width: 100%; }
1209→.hb-grid th, .hb-grid td { vertical-align: middle; }
1210→
1211→.hb-cell {
1212→  display: inline-block;
1213→  width: 16px;
1214→  height: 16px;
1215→  border-radius: 3px;
1216→  background: var(--muted);
1217→  flex-shrink: 0;
1218→  cursor: default;
1219→  transition: transform 0.1s ease;
1220→}
1221→
1222→.hb-cell:hover {
1223→  transform: scale(1.3);
1224→  z-index: 1;
1225→}
1226→
1227→.hb-cell.success { background: #10b981; }
1228→.hb-cell.skipped { background: var(--muted-foreground); opacity: 0.4; }
1229→.hb-cell.error { background: #ef4444; }
1230→
1231→@media (prefers-color-scheme: dark) {
1232→  .hb-cell.skipped { background: #52525b; opacity: 1; }
1233→}
1234→
1235→/* --- Mobile --- */
1236→
1237→@media (max-width: 639px) {
1238→  main.page-content { padding: 0.75rem; }
1239→  .card { padding: 1rem; }
1240→  button:not(.sm):not(.xs):not(.chat-tab):not(.chat-tab-new):not(.chat-tab-close) { min-height: 2.75rem; }
1241→  button.icon:not(.sm) { width: 2.75rem; height: 2.75rem; }
1242→  .chat-msg { max-width: 100%; }
1243→  .sidebar nav a { min-height: 2.75rem; display: flex; align-items: center; }
1244→
1245→  .chat-tab { padding: 0.25rem 0.375rem; font-size: 0.6875rem; }
1246→  .chat-tab-title { max-width: 80px; }
1247→  .chat-panel-messages .chat-msg { max-width: 100%; }
1248→}
