1<script>
2  document.addEventListener('DOMContentLoaded', () => {
3    if (window.chatFooter) {
4      window.chatFooter.openCanvas(<%= @page.id %>, '<%= @page.title.gsub("'", "\\\\'") %>', '<%= @page.slug %>'<% if @conversation %>, <%= @conversation.id %>, '<%= @conversation.title.gsub("'", "\\\\'") %>', '<%= @conversation.slug %>'<% end %>)
5    }
6  })
7</script>
8

   1/* ============================================
   2   OpenFang  shadcn/ui Design System
   3   Clean  Minimal  Terminal Green Accent  Light/Dark
   4   ============================================ */
   5
   6/* --- Design Tokens (Light Mode) --- */
   7:root {
   8  --background: #c8ccd1;
   9  --foreground: #0f172a;
  10  --card: #dde0e4;
  11  --card-foreground: #0f172a;
  12  --muted: #b8bcc2;
  13  --muted-foreground: #4a5568;
  14  --accent: #e8f5ec;
  15  --accent-foreground: #14532d;
  16  --primary: #16a34a;
  17  --primary-foreground: #ffffff;
  18  --border: #a8adb4;
  19  --ring: #16a34a;
  20  --input: #e8eaee;
  21  --destructive: #dc2626;
  22  --destructive-foreground: #ffffff;
  23
  24  /* Shadow scale */
  25  --shadow-sm: 0 1px 2px 0 rgb(0 0 0 / 0.05);
  26  --shadow-md: 0 4px 6px -1px rgb(0 0 0 / 0.1), 0 2px 4px -2px rgb(0 0 0 / 0.1);
  27  --shadow-lg: 0 10px 15px -3px rgb(0 0 0 / 0.1), 0 4px 6px -4px rgb(0 0 0 / 0.1);
  28
  29  /* Border radius scale */
  30  --radius-sm: 0.25rem;
  31  --radius-md: 0.375rem;
  32  --radius-lg: 0.5rem;
  33
  34  /* Backward-compat aliases */
  35  --fang-bg: var(--background);
  36  --fang-fg: var(--foreground);
  37  --fang-muted: var(--muted);
  38  --fang-muted-fg: var(--muted-foreground);
  39  --fang-accent: var(--primary);
  40  --fang-accent-fg: var(--primary-foreground);
  41  --fang-border: var(--border);
  42  --fang-card: var(--card);
  43}
  44
  45/* --- Dark Mode --- */
  46@media (prefers-color-scheme: dark) {
  47  :root {
  48    --background: #09090b;
  49    --foreground: #fafafa;
  50    --card: #18181b;
  51    --card-foreground: #fafafa;
  52    --muted: #27272a;
  53    --muted-foreground: #a1a1aa;
  54    --accent: #27272a;
  55    --accent-foreground: #22c55e;
  56    --primary: #16a34a;
  57    --primary-foreground: #ffffff;
  58    --border: #27272a;
  59    --ring: #16a34a;
  60    --input: var(--background);
  61    --destructive: #ef4444;
  62    --destructive-foreground: #ffffff;
  63
  64    --shadow-sm: 0 1px 2px 0 rgb(0 0 0 / 0.3);
  65    --shadow-md: 0 4px 6px -1px rgb(0 0 0 / 0.4), 0 2px 4px -2px rgb(0 0 0 / 0.3);
  66    --shadow-lg: 0 10px 15px -3px rgb(0 0 0 / 0.4), 0 4px 6px -4px rgb(0 0 0 / 0.3);
  67  }
  68}
  69
  70/* --- Element Base Styles --- */
  71
  72body {
  73  background: var(--background);
  74  color: var(--foreground);
  75  font-family: 'Inter', system-ui, sans-serif;
  76  line-height: 1.5;
  77}
  78
  79h1, h2, h3, h4, h5, h6 {
  80  font-weight: 600;
  81  line-height: 1.2;
  82  letter-spacing: -0.025em;
  83}
  84
  85h1 { font-size: 1.875rem; }
  86h2 { font-size: 1.5rem; }
  87h3 { font-size: 1.25rem; }
  88h4 { font-size: 1.125rem; }
  89h5 { font-size: 1rem; }
  90h6 { font-size: 0.875rem; }
  91
  92a {
  93  color: var(--primary);
  94  text-decoration: none;
  95  transition: color 0.2s;
  96}
  97a:hover {
  98  text-decoration: underline;
  99}
 100
 101button {
 102  display: inline-flex;
 103  align-items: center;
 104  justify-content: center;
 105  height: 2.5rem;
 106  padding: 0 1rem;
 107  background: var(--primary);
 108  color: var(--primary-foreground);
 109  font-family: inherit;
 110  font-size: 0.875rem;
 111  font-weight: 500;
 112  border: none;
 113  border-radius: var(--radius-md);
 114  cursor: pointer;
 115  transition: background 0.15s, box-shadow 0.15s;
 116  white-space: nowrap;
 117}
 118button:hover {
 119  background: color-mix(in srgb, var(--primary) 85%, black);
 120}
 121button:active {
 122  background: color-mix(in srgb, var(--primary) 75%, black);
 123}
 124button:focus-visible {
 125  outline: 2px solid var(--ring);
 126  outline-offset: 2px;
 127}
 128
 129input, textarea {
 130  background: var(--input);
 131  color: var(--foreground);
 132  font-family: inherit;
 133  font-size: 0.875rem;
 134  border: 1px solid var(--border);
 135  border-radius: var(--radius-md);
 136  padding: 0.5rem 0.75rem;
 137  width: 100%;
 138  outline: none;
 139  transition: border-color 0.2s, box-shadow 0.2s;
 140}
 141input:focus, textarea:focus {
 142  border-color: var(--ring);
 143  box-shadow: 0 0 0 2px color-mix(in srgb, var(--ring) 25%, transparent);
 144}
 145input::placeholder, textarea::placeholder {
 146  color: var(--muted-foreground);
 147}
 148
 149table {
 150  width: 100%;
 151  border-collapse: collapse;
 152  border: 1px solid var(--border);
 153  border-radius: var(--radius-lg);
 154  overflow: hidden;
 155}
 156th {
 157  font-weight: 500;
 158  font-size: 0.75rem;
 159  color: var(--muted-foreground);
 160  background: var(--muted);
 161  text-align: left;
 162  padding: 0.625rem 1rem;
 163}
 164td {
 165  border-bottom: 1px solid var(--border);
 166  padding: 0.625rem 1rem;
 167  font-size: 0.875rem;
 168}
 169tr:last-child td {
 170  border-bottom: none;
 171}
 172
 173/* --- Data Table Widget --- */
 174
 175.data-table-widget {
 176  display: flex;
 177  flex-direction: column;
 178  height: 100%;
 179}
 180
 181.data-table-scroll {
 182  overflow-y: auto;
 183  overflow-x: auto;
 184}
 185.data-table-scroll::-webkit-scrollbar { width: 6px; height: 6px; }
 186.data-table-scroll::-webkit-scrollbar-track { background: transparent; }
 187.data-table-scroll::-webkit-scrollbar-thumb { background: var(--border); border-radius: 3px; }
 188.data-table-scroll::-webkit-scrollbar-thumb:hover { background: var(--muted-foreground); }
 189
 190table.data-table {
 191  table-layout: fixed;
 192  min-width: max-content;
 193}
 194
 195.data-table-th {
 196  position: relative;
 197  user-select: none;
 198  white-space: nowrap;
 199}
 200
 201th.data-table-th[data-sortable="true"] {
 202  cursor: pointer;
 203}
 204th.data-table-th[data-sortable="true"]:hover {
 205  color: var(--foreground);
 206}
 207
 208th.sort-asc::after,
 209th.sort-desc::after {
 210  content: '';
 211  display: inline-block;
 212  width: 0;
 213  height: 0;
 214  margin-left: 0.375rem;
 215  vertical-align: middle;
 216  border-left: 4px solid transparent;
 217  border-right: 4px solid transparent;
 218}
 219th.sort-asc::after {
 220  border-bottom: 5px solid var(--primary);
 221}
 222th.sort-desc::after {
 223  border-top: 5px solid var(--primary);
 224}
 225
 226.col-resize-handle {
 227  position: absolute;
 228  right: 0;
 229  top: 0;
 230  bottom: 0;
 231  width: 4px;
 232  cursor: col-resize;
 233  background: transparent;
 234  transition: background 0.15s;
 235}
 236.col-resize-handle:hover {
 237  background: var(--primary);
 238}
 239
 240.data-table-truncate {
 241  display: block;
 242  max-width: 100%;
 243  overflow: hidden;
 244  text-overflow: ellipsis;
 245  white-space: nowrap;
 246}
 247
 248tr.data-table-next-page {
 249  border: none;
 250}
 251tr.data-table-next-page td {
 252  padding: 0;
 253  border: none;
 254  height: 1px;
 255}
 256
 257hr {
 258  border: none;
 259  border-top: 1px solid var(--border);
 260  margin: 1.5rem 0;
 261}
 262
 263code {
 264  background: var(--muted);
 265  color: var(--accent-foreground);
 266  padding: 0.15em 0.4em;
 267  font-size: 0.875em;
 268  border-radius: var(--radius-sm);
 269}
 270
 271pre {
 272  background: var(--muted);
 273  padding: 1rem;
 274  overflow-x: auto;
 275  margin: 0.75rem 0;
 276  border-radius: var(--radius-lg);
 277}
 278pre code {
 279  background: none;
 280  padding: 0;
 281}
 282
 283/* --- Button Variants --- */
 284
 285button.ghost {
 286  background: transparent;
 287  color: var(--foreground);
 288}
 289button.ghost:hover {
 290  background: var(--muted);
 291}
 292
 293button.outline {
 294  background: transparent;
 295  border: 1px solid var(--border);
 296  color: var(--foreground);
 297}
 298button.outline:hover {
 299  background: var(--muted);
 300}
 301
 302button.sm {
 303  height: 2.25rem;
 304  padding: 0 0.875rem;
 305  font-size: 0.8125rem;
 306}
 307
 308button.xs {
 309  height: 1.75rem;
 310  padding: 0 0.625rem;
 311  font-size: 0.75rem;
 312}
 313
 314button.icon {
 315  width: 2.5rem;
 316  height: 2.5rem;
 317  padding: 0;
 318}
 319button.icon.sm {
 320  width: 2rem;
 321  height: 2rem;
 322}
 323
 324/* --- Badge --- */
 325
 326.badge {
 327  display: inline-flex;
 328  align-items: center;
 329  padding: 0.125rem 0.5rem;
 330  font-size: 0.7rem;
 331  font-weight: 500;
 332  background: var(--muted);
 333  color: var(--muted-foreground);
 334  border-radius: 9999px;
 335}
 336.badge.success { background: #dcfce7; color: #166534; }
 337.badge.error   { background: #fee2e2; color: #991b1b; }
 338.badge.warning { background: #fef3c7; color: #92400e; }
 339.badge.info    { background: #dbeafe; color: #1e40af; }
 340
 341@media (prefers-color-scheme: dark) {
 342  .badge.success { background: #166534; color: #4ade80; }
 343  .badge.error   { background: #7f1d1d; color: #f87171; }
 344  .badge.warning { background: #78350f; color: #fbbf24; }
 345  .badge.info    { background: #1e3a5f; color: #60a5fa; }
 346}
 347
 348/* --- Card --- */
 349
 350.card {
 351  border: 1px solid var(--border);
 352  background: var(--card);
 353  padding: 1.5rem;
 354  border-radius: var(--radius-lg);
 355  box-shadow: var(--shadow-sm);
 356  transition: border-color 0.2s;
 357}
 358
 359/* --- Chat Messages (CLI-style) --- */
 360
 361.chat-msg {
 362  padding: 0.75rem 1rem;
 363  max-width: 100%;
 364  border-top: 1px solid var(--border);
 365}
 366.chat-msg:first-child { border-top: none; }
 367
 368.chat-msg.user {
 369  color: var(--foreground);
 370}
 371.chat-msg.ai {
 372  color: var(--foreground);
 373}
 374
 375/* Role label styling */
 376.chat-msg.user .msg-meta span:first-child {
 377  color: var(--primary);
 378  font-weight: 600;
 379}
 380.chat-msg.ai .msg-meta span:first-child {
 381  color: var(--muted-foreground);
 382  font-weight: 600;
 383}
 384
 385/* --- Sidebar --- */
 386
 387#sidebar-toggle {
 388  display: none;
 389}
 390
 391.sidebar {
 392  position: fixed;
 393  top: 0;
 394  left: 0;
 395  width: 16rem;
 396  height: 100dvh;
 397  background: var(--card);
 398  border-right: 1px solid var(--border);
 399  transform: translateX(-100%);
 400  transition: transform 0.2s ease;
 401  z-index: 50;
 402  overflow-y: auto;
 403  padding: 1.5rem;
 404  border-radius: 0 0.75rem 0.75rem 0;
 405}
 406
 407#sidebar-toggle:checked ~ .sidebar-container .sidebar {
 408  transform: translateX(0);
 409}
 410
 411.sidebar-container::before {
 412  content: '';
 413  position: fixed;
 414  inset: 0;
 415  background: rgba(0, 0, 0, 0.4);
 416  z-index: 40;
 417  opacity: 0;
 418  pointer-events: none;
 419  transition: opacity 0.2s ease;
 420}
 421#sidebar-toggle:checked ~ .sidebar-container::before {
 422  opacity: 1;
 423  pointer-events: auto;
 424}
 425
 426.sidebar nav a {
 427  display: block;
 428  padding: 0.5rem 0.75rem;
 429  color: var(--muted-foreground);
 430  font-size: 0.875rem;
 431  font-weight: 500;
 432  text-decoration: none;
 433  border-radius: var(--radius-md);
 434  transition: background 0.15s, color 0.15s;
 435}
 436.sidebar nav a:hover {
 437  background: var(--muted);
 438  color: var(--foreground);
 439  text-decoration: none;
 440}
 441
 442/* --- Utility Classes --- */
 443
 444.section-heading {
 445  font-size: 1.125rem;
 446  font-weight: 600;
 447  margin-bottom: 1rem;
 448  letter-spacing: -0.025em;
 449}
 450
 451.msg-meta {
 452  font-size: 0.6875rem;
 453  letter-spacing: 0.03em;
 454  color: var(--muted-foreground);
 455}
 456
 457.notification-unread {
 458  border-left: 3px solid var(--primary);
 459}
 460
 461/* --- Notifications Dropdown --- */
 462
 463.notifications-dropdown {
 464  position: absolute;
 465  top: calc(100% + 0.5rem);
 466  right: 0;
 467  z-index: 100;
 468  width: 360px;
 469  max-height: 480px;
 470  background: var(--card);
 471  border: 1px solid var(--border);
 472  border-radius: var(--radius-lg);
 473  box-shadow: var(--shadow-lg);
 474  display: flex;
 475  flex-direction: column;
 476  animation: ctx-menu-in 0.12s ease-out;
 477  transform-origin: top right;
 478}
 479
 480.notifications-dropdown-header {
 481  padding: 0.75rem 1rem;
 482  border-bottom: 1px solid var(--border);
 483  flex-shrink: 0;
 484}
 485
 486.notifications-dropdown-list {
 487  overflow-y: auto;
 488  flex: 1;
 489  min-height: 0;
 490}
 491
 492.notifications-dropdown-list .card {
 493  border-radius: 0;
 494  border-left: none;
 495  border-right: none;
 496  border-top: none;
 497  box-shadow: none;
 498  padding: 0.75rem 1rem;
 499}
 500.notifications-dropdown-list .card:last-child {
 501  border-bottom: none;
 502}
 503
 504.notifications-dropdown-list .notification-unread {
 505  border-left: 3px solid var(--primary);
 506}
 507
 508.notifications-dropdown-footer {
 509  border-top: 1px solid var(--border);
 510  padding: 0.5rem;
 511  flex-shrink: 0;
 512}
 513
 514.notifications-dropdown:not(.open) {
 515  animation: ctx-menu-out 0.1s ease-in forwards;
 516}
 517
 518@keyframes ctx-menu-out {
 519  from { opacity: 1; transform: scale(1); }
 520  to { opacity: 0; transform: scale(0.95); }
 521}
 522
 523@media (max-width: 639px) {
 524  .notifications-dropdown {
 525    width: calc(100vw - 1rem);
 526    right: -0.5rem;
 527  }
 528}
 529
 530/* --- Notification Card Submenu --- */
 531
 532.notification-submenu {
 533  position: relative;
 534}
 535
 536.notification-submenu > summary {
 537  cursor: pointer;
 538  list-style: none;
 539  display: inline-flex;
 540  align-items: center;
 541  gap: 0.375rem;
 542  height: 1.75rem;
 543  padding: 0 0.625rem;
 544  font-size: 0.75rem;
 545  font-weight: 500;
 546  background: transparent;
 547  border: 1px solid var(--border);
 548  color: var(--foreground);
 549  border-radius: var(--radius-md);
 550  transition: background 0.15s;
 551}
 552.notification-submenu > summary:hover {
 553  background: var(--muted);
 554}
 555.notification-submenu > summary:focus-visible {
 556  outline: 2px solid var(--ring);
 557  outline-offset: 2px;
 558}
 559.notification-submenu > summary::-webkit-details-marker {
 560  display: none;
 561}
 562.notification-submenu > summary::after {
 563  content: '';
 564  display: inline-block;
 565  width: 0;
 566  height: 0;
 567  border-left: 4px solid transparent;
 568  border-right: 4px solid transparent;
 569  border-top: 4px solid var(--muted-foreground);
 570  margin-left: 0.25rem;
 571}
 572
 573.notification-submenu-panel {
 574  position: absolute;
 575  bottom: calc(100% + 4px);
 576  left: 0;
 577  z-index: 50;
 578  min-width: 220px;
 579  max-width: 320px;
 580  background: var(--card);
 581  border: 1px solid var(--border);
 582  border-radius: var(--radius-lg);
 583  box-shadow: var(--shadow-md);
 584  padding: 0.25rem;
 585  animation: ctx-menu-in 0.12s ease-out;
 586}
 587
 588.notification-submenu-item {
 589  display: flex;
 590  flex-direction: column;
 591  align-items: flex-start;
 592  gap: 0.125rem;
 593  width: 100%;
 594  height: auto;
 595  padding: 0.375rem 0.625rem;
 596  background: transparent;
 597  color: var(--foreground);
 598  font-size: 0.8125rem;
 599  font-weight: 400;
 600  border: none;
 601  border-radius: 0.25rem;
 602  cursor: pointer;
 603  text-align: left;
 604}
 605.notification-submenu-item:hover {
 606  background: var(--muted);
 607}
 608
 609.notification-submenu-new {
 610  color: var(--primary);
 611}
 612
 613.notification-submenu-section {
 614  font-weight: 500;
 615}
 616
 617.notification-submenu-divider {
 618  height: 1px;
 619  background: var(--border);
 620  margin: 0.25rem 0;
 621}
 622
 623.nav-trigger {
 624  cursor: pointer;
 625  display: inline-flex;
 626  align-items: center;
 627  justify-content: center;
 628  width: 2.5rem;
 629  height: 2.5rem;
 630  border-radius: var(--radius-md);
 631  transition: background 0.15s;
 632}
 633.nav-trigger:hover {
 634  background: var(--muted);
 635}
 636.nav-trigger:focus-visible {
 637  outline: 2px solid var(--ring);
 638  outline-offset: 2px;
 639}
 640
 641.notification-badge {
 642  background: var(--primary);
 643  color: var(--primary-foreground);
 644  font-size: 0.6875rem;
 645  padding: 0.1rem 0.35rem;
 646  border-radius: 9999px;
 647  font-weight: 600;
 648}
 649
 650/* --- Prose Bubble (Markdown in Chat) --- */
 651
 652.prose-bubble > *:first-child { margin-top: 0; }
 653.prose-bubble > *:last-child { margin-bottom: 0; }
 654
 655.prose-bubble p { margin: 0.4em 0; }
 656.prose-bubble h1, .prose-bubble h2, .prose-bubble h3,
 657.prose-bubble h4, .prose-bubble h5, .prose-bubble h6 {
 658  font-weight: 600;
 659  margin: 0.6em 0 0.3em;
 660}
 661.prose-bubble h1 { font-size: 1.25em; }
 662.prose-bubble h2 { font-size: 1.15em; }
 663.prose-bubble h3 { font-size: 1.1em; }
 664
 665.prose-bubble ul, .prose-bubble ol { margin: 0.4em 0; padding-left: 1.4em; }
 666.prose-bubble ul { list-style: disc; }
 667.prose-bubble ol { list-style: decimal; }
 668.prose-bubble li { margin: 0.15em 0; }
 669
 670.prose-bubble code {
 671  background: var(--muted);
 672  color: var(--accent-foreground);
 673  padding: 0.15em 0.35em;
 674  font-size: 0.875em;
 675  border-radius: 0.25rem;
 676}
 677.prose-bubble pre {
 678  background: var(--muted);
 679  padding: 0.75em;
 680  overflow-x: auto;
 681  margin: 0.5em 0;
 682  border-radius: 0.375rem;
 683}
 684.prose-bubble pre code {
 685  background: none;
 686  padding: 0;
 687  font-size: 0.85em;
 688}
 689
 690.prose-bubble blockquote {
 691  border-left: 3px solid var(--border);
 692  padding-left: 0.75em;
 693  margin: 0.4em 0;
 694  color: var(--muted-foreground);
 695}
 696
 697.prose-bubble table { border-collapse: collapse; margin: 0.5em 0; width: 100%; }
 698.prose-bubble th, .prose-bubble td {
 699  border: 1px solid var(--border);
 700  padding: 0.3em 0.6em;
 701  text-align: left;
 702}
 703.prose-bubble th { font-weight: 600; background: var(--muted); }
 704
 705.prose-bubble a { color: var(--primary); text-decoration: underline; }
 706.prose-bubble a:hover { opacity: 0.8; }
 707
 708.prose-bubble hr { border: none; border-top: 1px solid var(--border); margin: 0.6em 0; }
 709
 710/* --- Agent Steps (streaming progress) --- */
 711
 712.agent-steps {
 713  display: flex;
 714  flex-direction: column;
 715  gap: 0.125rem;
 716  margin-bottom: 0.5rem;
 717}
 718.agent-step {
 719  display: flex;
 720  align-items: center;
 721  gap: 0.5rem;
 722  font-size: 0.75rem;
 723  color: var(--muted-foreground);
 724}
 725.agent-step-icon {
 726  width: 0.5rem;
 727  height: 0.5rem;
 728  border-radius: 50%;
 729  background: var(--muted-foreground);
 730  flex-shrink: 0;
 731}
 732/* Pulse the active (last) step */
 733.agent-step:last-child .agent-step-icon {
 734  background: var(--primary);
 735  animation: step-pulse 1.5s ease-in-out infinite;
 736}
 737@keyframes step-pulse {
 738  0%, 100% { opacity: 1; }
 739  50% { opacity: 0.3; }
 740}
 741
 742/* --- Action Button Feedback --- */
 743
 744.fang-spinner {
