 1registerWidget('clock', {
 2  init(el, metadata) {
 3    const tz = metadata.timezone || 'UTC'
 4    const display = el.querySelector('[data-clock-display]')
 5    if (!display) return
 6
 7    const tick = () => {
 8      display.textContent = new Date().toLocaleTimeString('en-US', {
 9        timeZone: tz,
10        hour12: false,
11        hour: '2-digit',
12        minute: '2-digit',
13        second: '2-digit'
14      })
15    }
16    tick()
17    el._clockInterval = setInterval(tick, 1000)
18  },
19
20  destroy(el) {
21    if (el._clockInterval) {
22      clearInterval(el._clockInterval)
23      delete el._clockInterval
24    }
25  }
26})
