 1registerWidget('chart', {
 2  init(el, metadata) {
 3    const chartContainer = el.querySelector('[data-fang-chart]')
 4    if (!chartContainer) return
 5
 6    const canvas = chartContainer.querySelector('canvas')
 7    if (!canvas) return
 8
 9    try {
10      const config = JSON.parse(chartContainer.dataset.fangChart)
11      el._chartInstance = Fang.renderChart(canvas, config)
12    } catch (e) {
13      console.error('[Chart Widget] Init failed:', e)
14    }
15  },
16
17  destroy(el) {
18    if (el._chartInstance) {
19      el._chartInstance.destroy()
20      delete el._chartInstance
21    }
22  }
23})
