  24  /* Shadow scale */
  25  --shadow-sm: 0 1px 2px 0 rgb(0 0 0 / 0.05);
  26  --shadow-md: 0 4px 6px -1px rgb(0 0 0 / 0.1), 0 2px 4px -2px rgb(0 0 0 / 0.1);
  27  --shadow-lg: 0 10px 15px -3px rgb(0 0 0 / 0.1), 0 4px 6px -4px rgb(0 0 0 / 0.1);
